SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_comp_paciente_pessoa (cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_seq_apresent_w	bigint;
ie_campo_paciente_w	varchar(15);

c01 CURSOR FOR
	SELECT	5 nr_seq_apres,
		'PESO' ie_componente
	
	
union

	SELECT	15 nr_seq_apres,
		'ALT' ie_componente
	
	
union

	select	20 nr_seq_apres,
		'IMC' ie_componente
	
	
union

	select	25 nr_seq_apres,
		'SC' ie_componente
	
	
union

	select	35 nr_seq_apres,
		'FC' ie_componente
	
	
union

	select	40 nr_seq_apres,
		'FR' ie_componente
	
	
union

	select	45 nr_seq_apres,
		'PAMIN' ie_componente
	
	
union

	select	50 nr_seq_apres,
		'PAMAX' ie_componente
	
	
union

	select	55 nr_seq_apres,
		'TS' ie_componente
	
	
union

	select	60 nr_seq_apres,
		'FRH' ie_componente
	
	
union

	select	65 nr_seq_apres,
		'CONV' ie_componente
	
	
union

	select	70 nr_seq_apres,
		'SI' ie_componente
	;


BEGIN

open	c01;
loop
fetch	c01 into
	nr_seq_apresent_w,
	ie_campo_paciente_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_sequencia_w
	from	pessoa_paciente_config
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	ie_campo_paciente	= ie_campo_paciente_w;

	if (nr_sequencia_w = 0) then
		begin

		select	nextval('pessoa_paciente_config_seq')
		into STRICT	nr_sequencia_w
		;

		insert	into pessoa_paciente_config(nr_sequencia,
			cd_pessoa_fisica,
			ie_campo_paciente,
			dt_atualizacao,
			nm_usuario,
			nr_seq_apresent)
		values (nr_sequencia_w,
			cd_pessoa_fisica_p,
			ie_campo_paciente_w,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_apresent_w);

		end;
	end if;

	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
