SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_comunicacao_alter_status (ie_status_agenda_p text, nr_seq_agenda_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


ds_titulo_w	varchar(255);
ds_mensagem_w	varchar(6000);
ds_agenda_w	varchar(255);
cd_perfil_w	integer;
nm_paciente_w	varchar(255);
cd_pessoa_fisica_w	varchar(10);
dt_agenda_w	timestamp;
cd_agenda_w bigint;

C01 CURSOR FOR
	SELECT	ds_titulo,
		ds_mensagem,
		cd_perfil
	from	agenda_envio_ci_status
	where	ie_status_agenda = ie_status_agenda_p
	and (cd_agenda = cd_agenda_w or coalesce(cd_agenda::text, '') = '');


BEGIN

begin
	select cd_agenda
	into STRICT cd_agenda_w
	from agenda_consulta
	where nr_sequencia = nr_seq_agenda_p;
exception
	when no_data_found then
		cd_agenda_w := null;
end;

open C01;
loop
fetch C01 into
	ds_titulo_w,
	ds_mensagem_w,
	cd_perfil_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	substr(max(obter_nome_medico_combo_agcons(b.cd_estabelecimento,b.cd_agenda,b.cd_tipo_agenda,coalesce(b.ie_ordenacao,'S'))),1,255),
		substr(max(a.nm_paciente),1,255),
		max(dt_agenda),
		max(a.cd_pessoa_fisica)
	into STRICT	ds_agenda_w,
		nm_paciente_w,
		dt_agenda_w,
		cd_pessoa_fisica_w
	from	agenda_consulta a,
		agenda b
	where	a.cd_agenda = b.cd_agenda
	and	a.nr_sequencia = nr_seq_agenda_p;

	ds_mensagem_w	:= substr(replace_macro(ds_mensagem_w, '@agenda', ds_agenda_w),1,6000);
	ds_mensagem_w	:= substr(replace_macro(ds_mensagem_w, '@horario', PKG_DATE_FORMATERS.TO_VARCHAR(dt_agenda_w, 'short', cd_estabelecimento_p, nm_usuario_p)),1,6000);
	ds_mensagem_w	:= substr(replace_macro(ds_mensagem_w, '@paciente', nm_paciente_w),1,6000);

	insert into comunic_interna(
			dt_comunicado,
			ds_titulo,
			ds_comunicado,
			nm_usuario,
			dt_atualizacao,
			ie_geral,
			nm_usuario_destino,
			ds_perfil_adicional,
			nr_sequencia,
			ie_gerencial,
			dt_liberacao,
			cd_estab_destino
		) values (
			clock_timestamp(),
			ds_titulo_w,
			ds_mensagem_w,
			'Tasy',
			clock_timestamp(),
			'N',
			'',
			cd_perfil_w||', ',
			nextval('comunic_interna_seq'),
			'N',
			clock_timestamp(),
			cd_estabelecimento_p);
	end;
end loop;
close C01;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
