SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_comunic_hist_funcionario (nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
 
nm_usuario_nrec_w		varchar(15);
nr_seq_tipo_hist_w		bigint;
dt_inicial_w			timestamp;
dt_final_w			timestamp;
ds_historico_w			varchar(3800);
ie_envia_comunic_interna_w	varchar(1);
nm_pessoa_fisica_w		varchar(60);

 
 

BEGIN 
 
begin 
	select	nm_usuario_nrec, 
		nr_seq_tipo_hist, 
		dt_inicial, 
		dt_final, 
		substr(ds_historico,1,3800), 
		substr(obter_nome_pf(cd_pessoa_fisica),1,60) 
	into STRICT	nm_usuario_nrec_w, 
		nr_seq_tipo_hist_w, 
		dt_inicial_w, 
		dt_final_w, 
		ds_historico_w, 
		nm_pessoa_fisica_w 
	from	pessoa_fisica_historico 
	where	nr_sequencia = nr_sequencia_p;
	 
	select	coalesce(ie_envia_comunic_interna,'N') 
	into STRICT	ie_envia_comunic_interna_w 
	from	tipo_hist_pessoa_fisica 
	where	nr_sequencia = nr_seq_tipo_hist_w;
exception 
when others then 
	ie_envia_comunic_interna_w := 'N';
 
end;	
 
if (ie_envia_comunic_interna_w = 'S') then 
 
	insert into comunic_interna(dt_comunicado, 
			ds_titulo, 
			ds_comunicado, 
			nm_usuario, 
			dt_atualizacao, 
			ie_geral, 
			nm_usuario_destino, 
			cd_perfil, 
			nr_sequencia, 
			ie_gerencial, 
			nr_seq_classif, 
			ds_perfil_adicional, 
			cd_setor_destino, 
			cd_estab_destino, 
			ds_setor_adicional, 
			dt_liberacao, 
			ds_grupo, 
			nm_usuario_oculto) 
	values		(clock_timestamp(), 
			substr(wheb_mensagem_pck.get_texto(306300,'NM_PESSOA_FISICA_W='||nm_pessoa_fisica_w),1,255), --substr('Inclusão de histórico do(a) funcionário(a) ' || nm_pessoa_fisica_w,1,255), 
			wheb_rtf_pck.get_texto_rtf(ds_historico_w), 
			nm_usuario_p, 
			clock_timestamp(), 
			'N', 
			nm_usuario_nrec_w, 
			null, 
			nextval('comunic_interna_seq'), 
			'N', 
			'', 
			'', 
			null, 
			'', 
			'', 
			'', 
			'', 
			'');
 
	commit;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
