CREATE OR REPLACE PROCEDURE gerar_comunic_just_cot ( nm_usuario_p text, nr_cot_compra_p bigint, ds_comunicado_p text, nm_usuario_dest_p text) AS $body$
DECLARE


texto_comunic_w		varchar(32000);
ds_titulo_w		varchar(255);




BEGIN
if (coalesce(nm_usuario_dest_p,'X') <> 'X')	then
	begin
	texto_comunic_w := 	wheb_mensagem_pck.get_texto(311899,'NR_COT_COMPRA_P='|| NR_COT_COMPRA_P ||';DS_COMUNICADO_P='|| DS_COMUNICADO_P); /*'Cotação :'||nr_cot_compra_p||chr(13)|| 'Justificativa : '||ds_comunicado_p;*/
	ds_titulo_w	:= 	wheb_mensagem_pck.get_Texto(311900); /*'Justificativa de alteração da cotação';	*/
	insert into comunic_interna(
		nr_sequencia,
		dt_comunicado,
		ds_titulo,
		ds_comunicado,
		nm_usuario,
		nm_usuario_destino,
		dt_atualizacao,
		ie_geral,
		ie_gerencial,
		dt_liberacao)
	values (	nextval('comunic_interna_seq'),
		clock_timestamp(),
		ds_titulo_w,
		texto_comunic_w,
		nm_usuario_p,
		nm_usuario_dest_p,
		clock_timestamp(),
		'N',
		'N',
		clock_timestamp());

	commit;
	end;
end if;
end;
$body$
LANGUAGE PLPGSQL
