CREATE OR REPLACE PROCEDURE gerar_comunic_prot_doc (nr_sequencia_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


/*
ie_opcao_p
1 - Criação de um protocolo
*/
ds_titulo_w				varchar(255);
ds_comunicacao_w		varchar(4000);
ds_obs_w				varchar(4000);
ie_tipo_protocolo_w		varchar(3);
ds_tipo_protocolo_w		varchar(255);
nm_usuario_destino_w	varchar(15);


BEGIN

if (coalesce(nr_sequencia_p,0) > 0) then
	begin
	select	ie_tipo_protocolo,
		substr(obter_valor_dominio(1047,ie_tipo_protocolo),1,255) ds_tipo_protocolo,
		ds_obs,
		substr(obter_usuario_pessoa(cd_pessoa_destino),1,15) nm_usuario_destino
	into STRICT	ie_tipo_protocolo_w,
		ds_tipo_protocolo_w,
		ds_obs_w,
		nm_usuario_destino_w
	from	protocolo_documento
	where	nr_sequencia = nr_sequencia_p;

	if (ie_opcao_p = '1') then
		ds_titulo_w	:= obter_desc_expressao(346397);--'Criação de um protocolo documento';
		ds_comunicacao_w:= substr(wheb_mensagem_pck.get_texto(306610,';NR_SEQUENCIA_P=' || nr_sequencia_p) || chr(13) || chr(10) || /*obter_desc_expressao(346398) || 'Criado o protocolo documento nº #@NR_SEQUENCIA_P#@' || nr_sequencia_p 'Criado o protocolo documento nº '*/
								  obter_desc_expressao(724396)/*'Tipo protocolo: '*/
 || ds_tipo_protocolo_w || chr(13) || chr(10) ||
								  'Obs.: ' || ds_obs_w,1,4000);
	end if;

	if (coalesce(nm_usuario_destino_w,'X') <> 'X') then
		CALL gerar_comunic_padrao(	clock_timestamp(),
					ds_titulo_w,
					ds_comunicacao_w,
					nm_usuario_p,
					'N',
					nm_usuario_destino_w,
					'N',
					null,
					'',
					'',
					'',
					clock_timestamp(),
					'',
					'');
	end if;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
