SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_conciliacao_caixa (nr_seq_extrato_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_saldo_inicial_w	double precision;
vl_saldo_final_w	double precision;
dt_saldo_inicial_w	timestamp;
dt_saldo_final_w	timestamp;
cd_registro_w		smallint;
cd_agencia_w		smallint;
cd_conta_w		integer;
ie_tipo_registro_w	varchar(1);
cd_historico_w		varchar(4);
ds_historico_w		varchar(25);
nr_documento_w		integer;
dt_movto_w		timestamp;
vl_extrato_w		double precision;
ie_deb_cred_w		varchar(1);
ie_natureza_w		varchar(1);
cd_categoria_w		varchar(3);
nr_seq_conta_w		bigint;
ie_eof_w		varchar(2);
ds_descricao_w		varchar(4000);
ds_conteudo_w		varchar(4000);
cd_agencia_ww		varchar(255);
cd_conta_ww 		varchar(255);
dt_movto_ww		varchar(255);
nr_documento_ww		varchar(255);
ds_historico_ww		varchar(255);
vl_extrato_ww		varchar(255);
ie_deb_cred_ww		varchar(255);

c01 CURSOR FOR
SELECT	ds_conteudo
from	w_interf_concil
where	nr_seq_conta	= nr_seq_conta_w
and	substr(ds_conteudo,2,5) <> 'Conta';


BEGIN

select	nr_seq_conta
into STRICT	nr_seq_conta_w
from	banco_extrato
where	nr_sequencia	= nr_seq_extrato_p;

open c01;
loop
fetch c01 into
	ds_conteudo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

ie_natureza_w	:= 'D';

select 	substr(ds_conteudo_w,1,position(';' in ds_conteudo_w)-1)
into STRICT	cd_conta_ww
;

ds_descricao_w := substr(ds_conteudo_w,length(cd_conta_ww)+2,length(ds_conteudo_w)-1);

select	substr(ds_descricao_w,1,position(';' in ds_descricao_w)-1)
into STRICT	dt_movto_ww
;

ds_descricao_w := substr(ds_descricao_w,length(dt_movto_ww)+2,length(ds_descricao_w)-1);

select	substr(ds_descricao_w,1,position(';' in ds_descricao_w)-1)
into STRICT	nr_documento_ww
;

ds_descricao_w := substr(ds_descricao_w,length(nr_documento_ww)+2,length(ds_descricao_w)-1);

select	substr(ds_descricao_w,1,position(';' in ds_descricao_w)-1)
into STRICT	ds_historico_ww
;

ds_descricao_w := substr(ds_descricao_w,length(ds_historico_ww)+2,length(ds_descricao_w)-1);

select	substr(ds_descricao_w,1,position(';' in ds_descricao_w)-1)
into STRICT	vl_extrato_ww
;

ds_descricao_w := substr(ds_descricao_w,length(vl_extrato_ww)+2,length(ds_descricao_w)-1);

select	ds_descricao_w
into STRICT	ie_deb_cred_ww
;

cd_agencia_w	:= substr(cd_conta_ww,2,4);
cd_conta_w 	:= substr(replace(cd_conta_ww,'"',''),8,9);
dt_movto_w	:= to_date(replace(dt_movto_ww,'"',''),'yyyymmdd');
nr_documento_w	:= replace(nr_documento_ww,'"','');
ds_historico_w	:= replace(ds_historico_ww,'"','');
vl_extrato_w	:= replace(replace(vl_extrato_ww,'"',''),'.',',');
ie_deb_cred_w	:= replace(ie_deb_cred_ww,'"','');

insert into banco_extrato_lanc(cd_agencia_origem,
	cd_categoria,
	ds_historico,
	dt_atualizacao,
	dt_atualizacao_nrec,
	dt_movimento,
	ie_conciliacao,
	ie_deb_cred,
	ie_natureza,
	nm_usuario,
	nm_usuario_nrec,
	nr_documento,
	nr_seq_extrato,
	nr_sequencia,
	vl_lancamento)
values (cd_agencia_w,
	cd_categoria_w,
	ds_historico_w,
	clock_timestamp(),
	clock_timestamp(),
	dt_movto_w,
	'N',
	ie_deb_cred_w,
	ie_natureza_w,
	nm_usuario_p,
	nm_usuario_p,
	nr_documento_w,
	nr_seq_extrato_p,
	nextval('banco_extrato_lanc_seq'),
	vl_extrato_w);

end loop;
close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
