CREATE OR REPLACE PROCEDURE gerar_conpaci_tipo_atend_conv (nr_interno_conta_p bigint, nm_usuario_p text, ds_tipo_informado_p text) AS $body$
DECLARE


ds_tipo_atendimento_w	conv_regra_tipo_atend.ds_tipo_atendimento%type:='';
nr_seq_regra_w		conv_regra_tipo_atend.nr_sequencia%type:=0;



BEGIN


if coalesce(ds_tipo_informado_p::text, '') = '' then --gerar conforme regra
	SELECT * FROM Obter_Tipo_Atend_Conv_Regra(Nr_Interno_Conta_p, ds_tipo_atendimento_w, nr_seq_regra_w) INTO STRICT ds_tipo_atendimento_w, nr_seq_regra_w;

	if coalesce(nr_seq_regra_w,0) > 0 then
		insert into conpaci_tipo_atend_conv(	nr_sequencia,
							dt_atualizacao,
							nm_usuario,
							dt_atualizacao_nrec,
							nm_usuario_nrec,
							nr_interno_conta,
							ds_tipo_atendimento,
							nr_seq_regra)
		values (	nextval('conpaci_tipo_atend_conv_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_interno_conta_p,
			ds_tipo_atendimento_w,
			nr_seq_regra_w);

	end if;
else --gerar conforme ds_tipo_informado_p informado pelo usuario
	insert into conpaci_tipo_atend_conv(	nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						nr_interno_conta,
						ds_tipo_atendimento,
						nr_seq_regra)
		values (	nextval('conpaci_tipo_atend_conv_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_interno_conta_p,
			ds_tipo_informado_p,
			null);
end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
