SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_consulta_oft_paciente ( cd_pessoa_fisica_p bigint, nr_atendimento_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_sequencia_w  	bigint;
nr_seq_status_w		bigint;
nr_seq_classificacao_w	bigint;
nr_seq_tipo_consulta_w	bigint;
cd_medico_w		varchar(50);
nr_seq_agenda_w		agenda_consulta.nr_atendimento%type;	
ie_agenda_w		varchar(2) := 'CI';


BEGIN

nr_seq_status_w := obter_param_usuario(3010, 18, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, nr_seq_status_w);

cd_medico_w  := obter_pessoa_fisica_usuario(wheb_usuario_pck.get_nm_usuario,'C');

CALL consiste_geracao_oftalmologia(nr_atendimento_p,nm_usuario_p,cd_estabelecimento_p);

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_atendimento_p > 0) then
	select  max(nr_seq_classificacao)
	into STRICT	nr_seq_classificacao_w
	from    atendimento_paciente
	where   nr_atendimento = nr_atendimento_p
	and	cd_estabelecimento = cd_estabelecimento_p;
end if;

if (nr_seq_classificacao_w IS NOT NULL AND nr_seq_classificacao_w::text <> '') then
	select max(nr_seq_tipo_consulta)
	into STRICT   nr_seq_tipo_consulta_w	
	from   classificacao_atendimento
	where  nr_sequencia = nr_seq_classificacao_w
	and    coalesce(cd_estabelecimento,cd_estabelecimento_p) = cd_estabelecimento_p
	and    coalesce(ie_situacao,'A') = 'A';
end if;


if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_atendimento_p > 0) then
	select nextval('oft_consulta_seq')
	into STRICT nr_sequencia_w
	;

	insert into oft_consulta(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_atendimento,
		nr_atend_consultorio,
		dt_consulta,
		cd_medico,
		dt_fim_consulta,
		nr_seq_status,
		nr_seq_tipo_consulta,
		cd_pessoa_fisica)
	values (
		nr_sequencia_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_atendimento_p,
		null,
		clock_timestamp(),
		cd_medico_w,
		null,
		nr_seq_status_w,
		nr_seq_tipo_consulta_w,
		cd_pessoa_fisica_p);
	commit;
	
	select 	max(nr_sequencia)
	into STRICT	nr_seq_agenda_w
	from   	agenda_paciente
	where  	ie_status_agenda not in ('L','C','E')
	and	nr_atendimento = nr_atendimento_p;
	
	if (coalesce(nr_seq_agenda_w::text, '') = '') then
		select 	max(nr_sequencia)
		into STRICT	nr_seq_agenda_w
		from   	agenda_consulta
		where  	ie_status_agenda not in ('L','C','E')
		and	nr_atendimento = nr_atendimento_p;
		
		ie_agenda_w := 'C';
	end if;
	if (nr_seq_agenda_w IS NOT NULL AND nr_seq_agenda_w::text <> '') then
		CALL executar_evento_agenda('GCO', ie_agenda_w, nr_seq_agenda_w, cd_estabelecimento_p, nm_usuario_p);
	end if;
elsif (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	
	select nextval('oft_consulta_seq')
	into STRICT nr_sequencia_w
	;

	insert into oft_consulta(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_pessoa_fisica,
		nr_atend_consultorio,
		dt_consulta,
		cd_medico,
		dt_fim_consulta,
		nr_seq_status)
	values (
		nr_sequencia_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		cd_pessoa_fisica_p,
		null,
		clock_timestamp(),
		cd_medico_w,
		null,
		nr_seq_status_w);
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
