CREATE OR REPLACE PROCEDURE gerar_consumo_dia ( dt_inicio_p timestamp, qt_dias_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE



/*QT_DIAS_P: Quantidade de dias para trás que a procedure deve fazer a consulta do consumo.
O consumo gravado no NR_DIA 0 (zero) , representa o consumo do dia que foi passado no parâmetro dt_inicio_p*/
dt_consumo_w			timestamp;
nr_dia_w				integer;
qt_consumo_w			double precision;
cd_material_w			integer;
qt_consumo_total_w		double precision;
qt_media_w			double precision;

c01 CURSOR FOR
SELECT	row_number() OVER () -1,
	dt_dia
from (
	SELECT	trunc(dt_dia,'dd') dt_dia
	from	dia_v
	where	dt_dia between trunc(dt_inicio_p,'dd') - qt_dias_p and trunc(dt_inicio_p,'dd')
	order by dt_dia desc) alias3
order by 1;

c02 CURSOR FOR
SELECT	m.cd_material_estoque,
	sum(qt_consumo) qt_consumo
from	movimento_estoque_v a,
	material m
where	a.cd_material_estoque = m.cd_material
and	a.cd_estabelecimento = cd_estabelecimento_p
and	(a.cd_centro_custo IS NOT NULL AND a.cd_centro_custo::text <> '')
and	m.ie_situacao = 'A'
and	trunc(a.dt_movimento_estoque,'dd') = trunc(dt_consumo_w,'dd')
group by m.cd_material_estoque;

c03 CURSOR FOR
SELECT	distinct
	cd_material
from	w_consumo_diario;


BEGIN

delete from w_consumo_diario;

open C01;
loop
fetch C01 into
	nr_dia_w,
	dt_consumo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	open C02;
	loop
	fetch C02 into
		cd_material_w,
		qt_consumo_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		insert into w_consumo_diario values (
			clock_timestamp(),
			nm_usuario_p,
			cd_material_w,
			nr_dia_w,
			qt_consumo_w,
			dt_consumo_w,
			0,
			0);
		end;
	end loop;
	close C02;
	end;
end loop;
close C01;

open C03;
loop
fetch C03 into
	cd_material_w;
EXIT WHEN NOT FOUND; /* apply on C03 */
	begin

	select	sum(qt_consumo)
	into STRICT	qt_consumo_total_w
	from	w_consumo_diario
	where	cd_material = cd_material_w
	and	nr_dia <> 0;

	qt_media_w := dividir(qt_consumo_total_w,qt_dias_p);

	update	w_consumo_diario
	set	qt_consumo_total = qt_consumo_total_w,
		qt_media = qt_media_w
	where	cd_material = cd_material_w;

	end;
end loop;
close C03;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
