SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_cons_lote_nfse_aut ( nr_seq_lote_p bigint, nm_usuario_p text, ie_tipo_transmissao_p bigint, ie_tipo_nota_p text, nr_seq_transmissao_p INOUT bigint) AS $body$
DECLARE


nr_sequencia_w				bigint;
nr_seq_lote_nota_w			bigint;
qt_registro_w				bigint;
nr_seq_tranmissao_w			nfe_transmissao_nf.nr_seq_transmissao%type;
tb_nr_seq_nf_w				pls_util_cta_pck.t_number_table;
tb_nr_rps_seq_w				pls_util_cta_pck.t_number_table;
tb_nr_ordem_w				pls_util_cta_pck.t_number_table;

--Notas transmissão
C01 CURSOR FOR
	SELECT	nr_seq_nota_fiscal,
			nr_rps_sequencial,
			nr_ordem
	from	nfe_transmissao_nf
	where	nr_seq_transmissao = nr_seq_tranmissao_w
	order by nr_ordem asc;

BEGIN

--Lote nfe e de transmissão
select	coalesce(max(nr_seq_lote_nota),0)
into STRICT	nr_seq_lote_nota_w
from	lote_nfe_transmissao
where	nr_sequencia = nr_seq_lote_p;

--Verifica quantidade de consultas para o lote, permite apenas 3
-- pois caso contrário as notas devem estar com problema e
--é preciso fazer o envio manual para que as transmissões automáticas não sejam comprometidas.
select 	count(1)
into STRICT	qt_registro_w
from   	nfe_transmissao
where  	ie_tipo_transmissao = '3'
and    	nr_seq_lote         = nr_seq_lote_p;

if (nr_seq_lote_p > 0) and (qt_registro_w < 4)then

	select 	nextval('nfe_transmissao_seq')
	into STRICT	nr_sequencia_w
	;

	--Transmissão para consulta
	insert into nfe_transmissao(nr_sequencia,
					 dt_atualizacao,
					 nm_usuario,
					 dt_atualizacao_nrec,
					 nm_usuario_nrec,
					 ie_tipo_nota,
					 ie_tipo_transmissao,
					 nr_seq_lote
					 )
	values (nr_sequencia_w,
						 clock_timestamp(),
						 nm_usuario_p,
						 clock_timestamp(),
						 nm_usuario_p,
						 ie_tipo_nota_p,
						 ie_tipo_transmissao_p,
						 nr_seq_lote_p );

	--Transmissão do lote
	select	max(nr_seq_transmissao)
	into STRICT	nr_seq_tranmissao_w
	from	nota_fiscal_lote_nfe
	where	nr_sequencia = nr_seq_lote_nota_w;

	--Limpa tabela
	tb_nr_seq_nf_w.delete;
	tb_nr_ordem_w.delete;
	tb_nr_rps_seq_w.delete;

	--Abre cursor
	open c01;
	loop
	fetch c01 bulk collect into tb_nr_seq_nf_w, tb_nr_rps_seq_w, tb_nr_ordem_w
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_nr_seq_nf_w.count = 0;
		-- Se encontrou algum registro faz o update
		forall i in tb_nr_seq_nf_w.first..tb_nr_seq_nf_w.last
			insert into nfe_transmissao_nf(nr_sequencia,
								dt_atualizacao,
								nm_usuario,
								dt_atualizacao_nrec,
								nm_usuario_nrec,
								nr_seq_transmissao,
								nr_seq_nota_fiscal,
								nr_rps_sequencial,
								nr_ordem)
				values (		nextval('nfe_transmissao_nf_seq'),
								clock_timestamp(),
								nm_usuario_p,
								clock_timestamp(),
								nm_usuario_p,
								nr_sequencia_w,
								tb_nr_seq_nf_w(i),
								tb_nr_rps_seq_w(i),
								tb_nr_ordem_w(i));
		commit;

	end loop;
	close c01;

	--Limpa tabela
	tb_nr_seq_nf_w.delete;
	tb_nr_ordem_w.delete;
	tb_nr_rps_seq_w.delete;
end if;

commit;

nr_seq_transmissao_p := nr_sequencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_cons_lote_nfse_aut ( nr_seq_lote_p bigint, nm_usuario_p text, ie_tipo_transmissao_p bigint, ie_tipo_nota_p text, nr_seq_transmissao_p INOUT bigint) FROM PUBLIC;

