CREATE OR REPLACE PROCEDURE gerar_contas_pre_fat_perda_spa (nr_seq_lote_p bigint, nr_seq_spa_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
					 
nr_interno_conta_w	conta_paciente.nr_interno_conta%type;
ie_tipo_convenio_w	spa.ie_tipo_convenio%type;
ie_tipo_convenio_spa_w spa.ie_tipo_convenio%type;
ie_data_contabil_spa_w	varchar(1);	
dt_spa_w		spa.dt_spa%type;				
			 
					 
C01 CURSOR FOR 
	SELECT 	coalesce(Obter_Tipo_Convenio(Obter_conv_conta(a.nr_interno_conta)),0) ie_tipo_convenio, 
		a.nr_interno_conta 
	from	pre_fatur_perda_conta a 
	where	nr_seq_perda = nr_seq_lote_p;
	

BEGIN 
 
 
ie_data_contabil_spa_w := coalesce(Obter_valor_param_usuario(7053, 23, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento),'N');
dt_spa_w		:=null;
 
if (ie_data_contabil_spa_w = 'S') then 
	begin 
		select	dt_spa 
		into STRICT	dt_spa_w 
		from	spa 
		where	nr_sequencia = nr_seq_spa_p;
	exception 
	when others then 
		dt_spa_w:=null;	
	end;
end if;
 
 
select	max(coalesce(ie_tipo_convenio,0)) 
into STRICT	ie_tipo_convenio_spa_w 
from	spa 
where	nr_sequencia = nr_seq_spa_p;
 
open C01;
loop 
fetch C01 into	 
	ie_tipo_convenio_w, 
	nr_interno_conta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
 
	if (ie_tipo_convenio_spa_w = ie_tipo_convenio_w) then 
	 
		insert into spa_movimento( 
			nr_sequencia, 
			dt_atualizacao, 
			cd_classificacao, 
			nr_interno_conta, 
			nr_seq_spa, 
			nr_seq_perda, 
			cd_convenio, 
			nr_atendimento, 
			nm_usuario, 
			dt_contabil			) 
			values (nextval('spa_movimento_seq'), 
			clock_timestamp(), 
			2, 
			nr_interno_conta_w, 
			nr_seq_spa_p, 
			nr_seq_lote_p, 
			Obter_conv_conta(nr_interno_conta_w), 
			Obter_Atendimento_Conta(nr_interno_conta_w), 
			nm_usuario_p, 
			dt_spa_w);
			 
	end if;	
		 
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
