CREATE OR REPLACE PROCEDURE gerar_conta_etapa_protocolo ( nr_interno_conta_p bigint, nr_seq_protocolo_p bigint, nr_seq_etapa_p bigint, cd_setor_atendimento_p bigint, nr_motivo_devol_p bigint, ds_observacao_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_motivo_devol_w	bigint;
cd_pessoa_fisica_w	varchar(10);
cd_setor_atendimento_w	integer;

BEGIN

select	nextval('conta_paciente_etapa_seq')
into STRICT	nr_sequencia_w
;

nr_motivo_devol_w := nr_motivo_devol_p;
if (nr_motivo_devol_w = 0) then
	nr_motivo_devol_w := null;
end if;

cd_setor_atendimento_w := cd_setor_atendimento_p;
if (cd_setor_atendimento_w = 0) then
	cd_setor_atendimento_w := null;
end if;

select 	max(cd_pessoa_fisica)
into STRICT	cd_pessoa_fisica_w
from 	usuario
where 	nm_usuario = nm_usuario_p;

insert into conta_paciente_etapa(
			nr_sequencia,
			nr_interno_conta,
			nr_seq_protocolo,
			dt_atualizacao,
			nm_usuario,
			dt_etapa,
			nr_seq_etapa,
			cd_setor_atendimento,
			cd_pessoa_fisica,
			nr_seq_motivo_dev,
			ds_observacao,
			nr_lote_barras,
			ie_etapa_protocolo)
		values (nr_sequencia_w,
			nr_interno_conta_p,
			nr_seq_protocolo_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nr_seq_etapa_p,
			cd_setor_atendimento_w,
			cd_pessoa_fisica_w,
			nr_motivo_devol_w,
			ds_observacao_p,
			null,
			'S');

CALL atualiza_final_etapa_conta(nr_sequencia_w, nm_usuario_p);

commit;

end;
$body$
