CREATE OR REPLACE PROCEDURE gerar_conta_financ_oc ( nr_ordem_compra_p bigint) AS $body$
DECLARE

 
cd_material_w			integer;
nr_item_oci_w			integer;
nr_seq_conta_financ_w	bigint;
cd_cgc_w			varchar(14);
cd_pessoa_fisica_w		varchar(255);
cd_estabelecimento_w		integer;
cd_centro_custo_w		integer;
nr_seq_proj_rec_w		bigint;

c01 CURSOR FOR 
SELECT	a.cd_material, 
	a.nr_item_oci, 
	a.cd_centro_custo, 
	a.nr_seq_proj_rec 
from	Ordem_Compra_Item a 
where	a.nr_ordem_compra	= nr_ordem_compra_p 
and	coalesce(a.nr_seq_conta_financ::text, '') = '' /*OS35595 - Fabio (15/06/2006)*/
 
order by 2;


BEGIN 
 
select	cd_estabelecimento, 
	cd_cgc_fornecedor, 
	cd_pessoa_fisica 
into STRICT	cd_estabelecimento_w, 
	cd_cgc_w, 
	cd_pessoa_fisica_w 
from	ordem_compra 
where	nr_ordem_compra	= nr_ordem_compra_p;
 
OPEN C01;
LOOP 
FETCH C01 into 
	cd_material_w, 
	nr_item_oci_w, 
	cd_centro_custo_w, 
	nr_seq_proj_rec_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	nr_seq_conta_financ_w := obter_conta_financeira('S', cd_estabelecimento_w, cd_material_w, null, null, null, null, cd_cgc_w, cd_centro_custo_w, nr_seq_conta_financ_w, null, null, null, null, null, nr_seq_proj_rec_w, null, cd_pessoa_fisica_w, null, null, null, null, null, null, null, null, null, null, null);
 
	if (coalesce(nr_seq_conta_financ_w,0) > 0) then 
		update	ordem_compra_item 
		set	nr_seq_conta_financ	= nr_seq_conta_financ_w 
		where	nr_ordem_compra	= nr_ordem_compra_p 
		and	nr_item_oci		= nr_item_oci_w;
	end if;
END LOOP;
CLOSE C01;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
