SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_conta_particular_percen ( nr_sequencia_p bigint, pr_particular_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_interno_conta_w	bigint;
cd_convenio_w		integer;
ie_tipo_convenio_w	smallint;
nr_sequencia_w		bigint;
cd_estabelecimento_w	smallint;
cd_convenio_glosa_w	integer;
cd_categoria_glosa_w	varchar(10);
vl_material_w		double precision;


BEGIN 
 
begin 
select 	coalesce(max(b.nr_interno_conta),0), 
	coalesce(max(b.cd_convenio_parametro),0), 
	coalesce(sum(a.vl_material),0), 
	coalesce(max(b.cd_estabelecimento),0) 
into STRICT	nr_interno_conta_w, 
	cd_convenio_w, 
	vl_material_w, 
	cd_estabelecimento_w 
from 	material_atend_paciente	a, 
	conta_paciente		b 
where 	a.nr_sequencia = nr_sequencia_p 
and 	a.nr_interno_conta = b.nr_interno_conta;
exception 
	when others then 
		cd_convenio_w		:= 0;
		nr_interno_conta_w	:= 0;
		cd_estabelecimento_w	:= 0;
end;
 
select 	coalesce(max(ie_tipo_convenio),0) 
into STRICT	ie_tipo_convenio_w 
from 	convenio 
where 	cd_convenio = cd_convenio_w;
 
if (ie_tipo_convenio_w = 1) then --Conta Particular 
	--R.aise_application_error(-20011, 'O material já está em uma conta Particular ' || '#@#@'); 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(263358);
end if;
 
SELECT * FROM obter_convenio_particular(cd_estabelecimento_w, cd_convenio_glosa_w, cd_categoria_glosa_w) INTO STRICT cd_convenio_glosa_w, cd_categoria_glosa_w;
nr_sequencia_w := Duplicar_Mat_Paciente(nr_sequencia_p, nm_usuario_p, 'N', nr_sequencia_w);
 
update 	material_atend_paciente 
set	ie_valor_informado	= 'S', 
	vl_material		= dividir(vl_material * pr_particular_p,100), 
	cd_situacao_glosa	= 0, 
	qt_material		= 0, 
	cd_convenio		= cd_convenio_glosa_w, 
	cd_categoria		= cd_categoria_glosa_w, 
	nr_interno_conta	 = NULL, 
	ds_observacao		= substr(wheb_mensagem_pck.get_texto(305920,'PR_PARTICULAR=' || pr_particular_p),1,255) 
where	nr_sequencia		= nr_sequencia_w;
 
CALL Atualiza_preco_material(nr_sequencia_w, nm_usuario_p);
 
select 	vl_material 
into STRICT	vl_material_w 
from 	material_atend_paciente 
where 	nr_sequencia = nr_sequencia_w;
 
update material_atend_paciente 
set	ie_valor_informado	= 'S', 
	vl_material		= vl_material - vl_material_w, 
	ds_observacao		= substr(wheb_mensagem_pck.get_texto(305920,'PR_PARTICULAR=' || pr_particular_p),1,255) 
where	nr_sequencia		= nr_sequencia_p;
 
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
