CREATE OR REPLACE PROCEDURE gerar_conta_periodo_selec ( nr_interno_conta_p bigint, nm_usuario_p text, cd_convenio_p bigint, cd_categoria_p text) AS $body$
DECLARE

 
 
 
nr_interno_conta_w bigint;				
 

BEGIN 
 
select	nextval('conta_paciente_seq') 
into STRICT	nr_interno_conta_w
;
 
 
insert into Conta_Paciente( 
	nr_atendimento, 
	dt_acerto_conta, 
	ie_status_acerto, 
	dt_periodo_inicial, 
	dt_periodo_final, 
	dt_atualizacao, 
	nm_usuario, 
	cd_convenio_parametro, 
	nr_protocolo, 
	dt_mesano_referencia, 
	dt_mesano_contabil, 
	cd_convenio_calculo, 
	cd_categoria_calculo, 
	nr_interno_conta, 
	nr_seq_protocolo, 
	cd_categoria_parametro, 
	ds_inconsistencia, 
	dt_recalculo, 
	cd_estabelecimento, 
	vl_desconto, 
	vl_conta) 
SELECT	a.nr_atendimento,	 
	a.dt_acerto_conta, 
	1, 
	a.dt_periodo_inicial,	 
	a.dt_periodo_final, 
	clock_timestamp(), 
	nm_usuario_p, 
	cd_convenio_p, 
	'0', 
	a.dt_mesano_referencia, 
	a.dt_mesano_contabil, 
	cd_convenio_p, 
	cd_categoria_p, 
	nr_interno_conta_w, 
	null, 
