CREATE OR REPLACE PROCEDURE gerar_conteudo_pauta_js ( ds_lista_pauta_p text, nm_usuario_p text, nr_seq_ata_p bigint default null) AS $body$
DECLARE


ds_conteudo_w		ata_reuniao_pauta.ds_conteudo%type;
ds_pauta_w		ata_reuniao_pauta.ds_pauta%type;
nr_seq_ata_w		proj_ata.nr_sequencia%type;

c01 CURSOR FOR
	SELECT	a.ds_conteudo,
		a.ds_pauta,
		b.nr_sequencia
	from	ata_reuniao_pauta a,
		proj_ata b
	where	a.nr_seq_reuniao = b.nr_seq_reuniao
	and	(a.ds_conteudo IS NOT NULL AND a.ds_conteudo::text <> '')
	and	obter_se_contido_char(a.nr_sequencia,ds_lista_pauta_p) = 'S'
	and	b.nr_sequencia = coalesce(nr_seq_ata_p,b.nr_sequencia);


BEGIN

if ((trim(both ds_lista_pauta_p) IS NOT NULL AND (trim(both ds_lista_pauta_p))::text <> '')) then
	begin
	open c01;
	loop
	fetch c01 into
		ds_conteudo_w,
		ds_pauta_w,
		nr_seq_ata_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		insert into proj_ata_conteudo(ds_conteudo,
			ds_titulo,
			nm_usuario_nrec,
			dt_atualizacao_nrec,
			nm_usuario,
			dt_atualizacao,
			nr_seq_pauta,
			nr_sequencia,
			nr_seq_ata)
		values (ds_conteudo_w,
			ds_pauta_w,
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			null,
			nextval('proj_ata_conteudo_seq'),
			nr_seq_ata_w);
		end;
	end loop;
	close c01;

	commit;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
