CREATE OR REPLACE PROCEDURE gerar_conteudo_pauta ( nr_seq_pauta_p bigint, nr_seq_proj_ata_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_conteudo_w		varchar(4000);
ds_pauta_w		varchar(255);
nr_seq_ata_w		bigint;


BEGIN

select substr(a.ds_conteudo,1,4000),
	a.ds_pauta,
	b.nr_sequencia
into STRICT	ds_conteudo_w,
	ds_pauta_w,
	nr_seq_ata_w
from	ata_reuniao_pauta a,
	proj_ata b
where	a.nr_sequencia = nr_seq_pauta_p
and	a.nr_seq_reuniao = b.nr_seq_reuniao
and	b.nr_sequencia = nr_seq_proj_ata_p;

if (ds_conteudo_w IS NOT NULL AND ds_conteudo_w::text <> '') then
	insert into proj_ata_conteudo(ds_conteudo,
		ds_titulo,
		nm_usuario,
		dt_atualizacao,
		nr_seq_pauta,
		nr_sequencia,
		nr_seq_ata)
	values (ds_conteudo_w,
		ds_pauta_w,
		nm_usuario_p,
		clock_timestamp(),
		null,
		nextval('proj_ata_conteudo_seq'),
		nr_seq_ata_w);
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
