CREATE OR REPLACE PROCEDURE gerar_controle_pessoa_html5 ( cd_tipo_controle_p bigint, nm_usuario_p text, cd_setor_atendimento_p bigint, ie_filtra_aniversario_p text, dt_aniversario_p timestamp, ie_somente_medico_p text, ie_especialidade_p bigint, ie_somente_funcionario_p text, ie_filtra_data_passagem_p text, dt_inicio_passagem_p timestamp, dt_final_passagem_p timestamp, ie_corpo_clinico_p text, nr_seq_vinculo_p bigint, nr_seq_categoria_p bigint, ie_obito_P text) AS $body$
DECLARE




cd_pessoa_fisica_w		pessoa_fisica.cd_pessoa_fisica%type;

qt_registros_w			bigint;



C01 CURSOR FOR

SELECT	p.cd_pessoa_fisica

from	pessoa_fisica p

where	(exists(	SELECT	1

	  	from	atendimento_paciente b,

	  		atend_paciente_unidade a

	  	where	a.cd_setor_atendimento = cd_setor_atendimento_p

	  	and	a.nr_atendimento = b.nr_atendimento

	  	and	b.cd_pessoa_fisica = p.cd_pessoa_fisica

		and	((coalesce(p.dt_obito::text, '') = '' and ie_obito_p = 'S') or (ie_obito_p = 'N'))

   and	((ie_filtra_data_passagem_p = 'N') or ((ie_filtra_data_passagem_p = 'S') and (trunc(a.dt_entrada_unidade) between trunc(dt_inicio_passagem_p) and fim_dia(dt_final_passagem_p))))

			) or cd_setor_atendimento_p = 0)

and	((ie_filtra_aniversario_p = 'N') or ((ie_filtra_aniversario_p = 'S') and (PKG_DATE_UTILS.extract_field('MONTH',p.dt_nascimento) = PKG_DATE_UTILS.extract_field('MONTH',dt_aniversario_p)  )))

and	(exists(	select	1

		from	medico m

		where	m.cd_pessoa_fisica = p.cd_pessoa_fisica

		and	(

			((Obter_Especialidade_medico(m.cd_pessoa_fisica,'E'))::numeric  = ie_especialidade_p or ie_especialidade_p = 0) and

			((m.nr_seq_categoria = nr_seq_categoria_p) or (coalesce(nr_seq_categoria_p,0) = 0)) and

			((m.ie_corpo_clinico = ie_corpo_clinico_p) or (coalesce(ie_corpo_clinico_p,'N') = 'N')) and

			((m.ie_vinculo_medico= nr_seq_vinculo_p) or (coalesce(nr_seq_vinculo_p,0) = 0))

			)

			) or ie_somente_medico_p = 'N')

and	((ie_funcionario = 'S') or ie_somente_funcionario_p = 'N');




BEGIN



open C01;

loop

fetch C01 into

	cd_pessoa_fisica_w;

EXIT WHEN NOT FOUND; /* apply on C01 */

	begin



	select 	count(*)

	into STRICT	qt_registros_w

	from 	controle_pessoa

	where	cd_pessoa_fisica = cd_pessoa_fisica_w

	and	cd_tipo_controle = cd_tipo_controle_p;



	if (coalesce(qt_registros_w,0) = 0) then



		insert into controle_pessoa(	nr_sequencia,

					cd_tipo_controle,

					dt_atualizacao,

					nm_usuario,

					cd_pessoa_fisica,

					ie_situacao)

				values (	nextval('controle_pessoa_seq'),

					cd_tipo_controle_p,

					clock_timestamp(),

					nm_usuario_p,

					cd_pessoa_fisica_w,

					'A');



	else



		update 	controle_pessoa

		set 	dt_atualizacao = clock_timestamp(),

			nm_usuario = nm_usuario_p

		where	cd_tipo_controle = cd_tipo_controle_p

		and	cd_pessoa_fisica = cd_pessoa_fisica_w;



	end if;



	end;

end loop;

close c01;



commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
