CREATE OR REPLACE PROCEDURE gerar_controle_proc_aih ( nr_seq_protocolo_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
qt_registros_w		bigint;
qt_processo_w		bigint;
nr_interno_conta_w		bigint;
vl_conta_w		double precision;
nr_aih_w			bigint;
nr_seq_aih_w		bigint;
nr_sequencia_w		bigint;

 
C01 CURSOR FOR 
SELECT	a.nr_interno_conta, 
	a.vl_conta, 
	b.nr_aih, 
	b.nr_sequencia 
from	sus_aih b, 
	conta_paciente a 
where	a.nr_seq_protocolo = nr_seq_protocolo_p 
and	coalesce(a.ie_cancelamento::text, '') = '' 
and	b.nr_interno_conta	= a.nr_interno_conta 
order by  a.nr_interno_conta;


BEGIN 
qt_registros_w	:= 0;
qt_processo_w	:= 0;
 
select	count(*) 
into STRICT	qt_registros_w 
from	sus_aih_controle_proc 
where	nr_seq_protocolo = nr_seq_protocolo_p;
 
if (qt_registros_w	> 0) then 
	begin 
	select	count(*) 
	into STRICT	qt_processo_w 
	from	sus_aih_controle_proc 
	where	nr_seq_protocolo = nr_seq_protocolo_p 
	and	(nr_processo IS NOT NULL AND nr_processo::text <> '');
	end;
end if;
 
if (qt_registros_w	> 0) and (qt_processo_w	= 0) then 
	begin 
	delete from sus_aih_controle_proc 
	where	nr_seq_protocolo = nr_seq_protocolo_p;
	commit;
	end;
end if;
 
if (qt_processo_w = 0) then 
	begin 
	open C01;
	loop 
	fetch C01 into 
		nr_interno_conta_w, 
		vl_conta_w, 
		nr_aih_w, 
		nr_seq_aih_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		select	nextval('sus_aih_controle_proc_seq') 
		into STRICT	nr_sequencia_w 
		;
 
		insert into	sus_aih_controle_proc( 
			NR_SEQUENCIA, 
			DT_ATUALIZACAO, 
			NM_USUARIO, 
			DT_ATUALIZACAO_NREC, 
			NM_USUARIO_NREC, 
			NR_INTERNO_CONTA, 
			NR_AIH, 
			NR_SEQ_AIH, 
			NR_PROCESSO, 
			VL_ORIGINAL, 
			VL_PAGO, 
			VL_REJEITADO, 
			NR_SEQ_PROTOCOLO) 
		values (nr_sequencia_w, 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			nr_interno_conta_w, 
			nr_aih_w, 
			nr_seq_aih_w, 
			null, 
			vl_conta_w, 
			null, 
			null, 
			nr_seq_protocolo_p);
		end;
	end loop;
	close C01;
	end;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
