CREATE OR REPLACE PROCEDURE gerar_control_terc_auto ( cd_estabelecimento_p bigint, nr_seq_tp_reserva_p bigint, nr_seq_nut_reserva_p bigint, nm_usuario_p text, cd_pessoa_fisica_p text) AS $body$
DECLARE

 
nr_seq_terceiro_w	bigint  := 0;

nr_seq_operacao_w    nut_tipo_reserva_control.nr_seq_operacao%type;	
cd_material_w    	nut_tipo_reserva_control.cd_material%type;	
cd_procedimento_w    nut_tipo_reserva_control.cd_procedimento%type;	
ie_origem_proced_w   nut_tipo_reserva_control.ie_origem_proced%type;	
qt_operacao_w    	nut_tipo_reserva_control.qt_operacao%type;	
vl_operacao_w    	nut_tipo_reserva_control.vl_operacao%type;	
 
c01 CURSOR FOR 
	SELECT 	nr_seq_operacao, 
		cd_material, 
		cd_procedimento, 
		ie_origem_proced, 
		qt_operacao, 
		vl_operacao 
	from  	NUT_TIPO_RESERVA_CONTROL 
	where 	nr_seq_tp_reserva = nr_seq_tp_reserva_p;
				

BEGIN 
 
open	c01;
	loop 
	fetch	c01 into 
		nr_seq_operacao_w, 
		cd_material_w, 
		cd_procedimento_w, 
		ie_origem_proced_w, 
		qt_operacao_w, 
		vl_operacao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	 
	CALL gerar_terceiro_oper_reserv( 
				cd_estabelecimento_p, 
				nr_seq_operacao_w, 
				clock_timestamp(), 
				cd_material_w, 
				ie_origem_proced_w, 
				cd_procedimento_w, 
				qt_operacao_w, 
				vl_operacao_w, 
				nm_usuario_p, 
				cd_pessoa_fisica_p, 
				nr_seq_nut_reserva_p, 
				'M');
		 
end loop;
close c01;
 
commit;
 
end;
