CREATE OR REPLACE PROCEDURE gerar_convenio_retorno_glosa (nr_seq_item_glosa_p bigint, nm_usuario_p text) AS $body$
BEGIN

insert	into convenio_retorno_glosa(cd_autorizacao_compl,
	cd_especialidade,
	cd_item_convenio,
	cd_material,
	cd_pessoa_fisica,
	cd_procedimento,
	cd_setor_atendimento,
	cd_setor_responsavel,
	ds_observacao,
	dt_atualizacao,
	dt_atualizacao_nrec,
	dt_execucao,
	ie_atualizacao,
	ie_emite_conta,
	ie_glosa_geral,
	ie_origem_proced,
	nm_usuario,
	nm_usuario_nrec,
	nr_seq_matpaci,
	nr_seq_partic,
	nr_seq_propaci,
	nr_seq_ret_item,
	nr_seq_trans_financ,
	nr_sequencia,
	qt_cobrada,
	qt_glosa,
	vl_cobrado,
	vl_glosa)
SELECT	a.cd_autorizacao_compl,
	a.cd_especialidade,
	a.cd_item_convenio,
	a.cd_material,
	a.cd_pessoa_fisica,
	a.cd_procedimento,
	a.cd_setor_atendimento,
	a.cd_setor_responsavel,
	wheb_mensagem_pck.get_Texto(312082), /*'Gerado pelo sistema com o saldo restante do item. Opção Glosar saldo dos itens selecionados',*/
	clock_timestamp(),
	clock_timestamp(),
	a.dt_execucao,
	a.ie_atualizacao,
	a.ie_emite_conta,
	a.ie_glosa_geral,
	a.ie_origem_proced,
	nm_usuario_p,
	nm_usuario_p,
	a.nr_seq_matpaci,
	a.nr_seq_partic,
	a.nr_seq_propaci,
	a.nr_seq_ret_item,
	a.nr_seq_trans_financ,
	nextval('convenio_retorno_glosa_seq'),
	a.qt_cobrada,
	coalesce(a.qt_cobrada,0) - coalesce(a.qt_glosa,0) qt_glosa,
	a.vl_cobrado,
	coalesce(a.vl_cobrado,0) - coalesce(a.vl_glosa,0) - coalesce(a.vl_pago_digitado,0) vl_glosa
from	convenio_retorno_glosa a
where (coalesce(a.vl_cobrado,0) - coalesce(a.vl_glosa,0) - coalesce(a.vl_pago_digitado,0))	> 0
and	a.nr_sequencia	= nr_seq_item_glosa_p;

commit;

end;
$body$
