CREATE OR REPLACE PROCEDURE gerar_convenio_sus_primario ( nr_atendimento_p bigint, nm_usuario_p text, cd_categoria_p text, cd_convenio_p bigint) AS $body$
DECLARE

  dt_inicio_vigencia_w timestamp;
  count_w bigint;

BEGIN

    SELECT COUNT(*) INTO STRICT count_w FROM atend_categoria_convenio WHERE nr_atendimento = nr_atendimento_p;

    IF count_w = 0 THEN
		BEGIN
			SELECT coalesce(obter_data_entrada(nr_atendimento_p),clock_timestamp())
			INTO STRICT dt_inicio_vigencia_w
			;

			INSERT INTO atend_categoria_convenio(
				nr_atendimento,
				cd_convenio,
				cd_categoria,
				dt_inicio_vigencia,
				dt_atualizacao,
				nm_usuario,
				nr_seq_interno
			)
			VALUES (
				nr_atendimento_p,
				cd_convenio_p,
				cd_categoria_p,
				dt_inicio_vigencia_w,
				clock_timestamp(),
				nm_usuario_p,
				nextval('atend_categoria_convenio_seq')
			);
			COMMIT;
		END;
    END IF;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
