CREATE OR REPLACE PROCEDURE gerar_conv_receb_adic (nr_seq_receb_p bigint, vl_adicional_p bigint, nr_seq_trans_financ_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (coalesce(vl_adicional_p,0) <> 0) and (nr_seq_trans_financ_p IS NOT NULL AND nr_seq_trans_financ_p::text <> '') then

	insert	into convenio_receb_adic(dt_atualizacao,
		nm_usuario,
		nr_lote_contabil,
		nr_seq_receb,
		nr_seq_trans_financ,
		nr_sequencia,
		vl_adicional)
	values (clock_timestamp(),
		nm_usuario_p,
		0,
		nr_seq_receb_p,
		nr_seq_trans_financ_p,
		nextval('convenio_receb_adic_seq'),
		vl_adicional_p);
end if;

end;
$body$
