CREATE OR REPLACE PROCEDURE gerar_copartic_conta_partic (nr_seq_autorizacao_p procedimento_autorizado.nr_sequencia_autor%type, nm_usuario_p procedimento_autorizado.nm_usuario%type) AS $body$
DECLARE



nr_seq_proc_partic  procedimento_paciente.nr_sequencia%type;

c_proc_guia_wint CURSOR FOR
SELECT  c.nr_sequencia nr_seq_procedimento,
        c.cd_convenio
from    procedimento_guia_wint a,
        procedimento_autorizado b,
        procedimento_paciente c
where   b.nr_sequencia_autor = nr_seq_autorizacao_p
and     b.nr_sequencia = a.nr_seq_proc_autor
and     b.nr_atendimento = c.nr_atendimento
and     ((c.nr_seq_proc_autor = b.nr_sequencia) or
        (c.nr_sequencia_prescricao = b.nr_seq_prescricao AND c.nr_prescricao = b.nr_prescricao ))
and     a.vl_coparticipacao <> 0;

BEGIN

for c_proc_guia_wint_p in c_proc_guia_wint loop
        CALL atualiza_preco_procedimento(c_proc_guia_wint_p.nr_seq_procedimento, c_proc_guia_wint_p.cd_convenio, nm_usuario_p);

        begin
        select  a.nr_sequencia
        into STRICT    nr_seq_proc_partic
        from    procedimento_paciente a
        where   a.nr_seq_proc_princ = c_proc_guia_wint_p.nr_seq_procedimento;
        exception
                when no_data_found then
                        nr_seq_proc_partic := 0;
                when too_many_rows then
                        nr_seq_proc_partic := 0;
        end;

        if (nr_seq_proc_partic > 0)then
                CALL atualiza_preco_proc_amb(nr_seq_proc_partic, nm_usuario_p);
        end if;
end loop;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
