CREATE OR REPLACE PROCEDURE gerar_cotacao_moeda ( cd_moeda_p bigint, vl_cotacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	cotacao_moeda
where	cd_moeda		= cd_moeda_p
and	trunc(dt_cotacao,'dd')	= trunc(clock_timestamp(),'dd');

if (qt_existe_w > 0) then
	update	cotacao_moeda
	set	vl_cotacao		= vl_cotacao_p
	where	cd_moeda		= cd_moeda_p
	and	trunc(dt_cotacao,'dd')	= trunc(clock_timestamp(),'dd');
else
	insert into cotacao_moeda(
		cd_moeda,
		dt_cotacao,
		vl_cotacao,
		dt_atualizacao,
		nm_usuario)
	values (	cd_moeda_p,
		trunc(clock_timestamp(),'dd'),
		vl_cotacao_p,
		clock_timestamp(),
		nm_usuario_p);

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
