SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_cota_moderadora ( nr_transacao_finan_p TRANSACAO_FINANCEIRA.NR_SEQUENCIA%type, nr_atendimento_p ADIANTAMENTO.NR_ATENDIMENTO%type ) AS $body$
DECLARE


ie_conta_moderadora_w TRANSACAO_FINANCEIRA.IE_COTA_MODERADORA%type;


BEGIN

SELECT  max(IE_COTA_MODERADORA)
INTO STRICT    ie_conta_moderadora_w
FROM    TRANSACAO_FINANCEIRA
WHERE   NR_SEQUENCIA = nr_transacao_finan_p;

IF ie_conta_moderadora_w = 'S' THEN

        UPDATE  ADIANTAMENTO 
        SET     VL_ADIANTAMENTO = OBTER_DADOS_ANTECIP(nr_atendimento_p,nr_transacao_finan_p,'VLC')
        WHERE   NR_ATENDIMENTO = nr_atendimento_p;

        COMMIT;

END IF;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
