CREATE OR REPLACE PROCEDURE gerar_criterio_avaliacao ( nr_sequencia_p bigint, nr_seq_criterio_p bigint, ie_grau_satisfacao_p text, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w			bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	man_os_crit_avaliacao
where	nr_ordem_servico = nr_sequencia_p
and	nr_seq_criterio = nr_seq_criterio_p;

if (qt_existe_w > 0) then

	if (coalesce(ie_grau_satisfacao_p,'0') = '0') then

		delete from man_os_crit_avaliacao
		where	nr_ordem_servico = nr_sequencia_p
		and	nr_seq_criterio = nr_seq_criterio_p;
	else
		update	man_os_crit_avaliacao
		set	ie_grau_satisfacao = ie_grau_satisfacao_p
		where	nr_ordem_servico = nr_sequencia_p
		and	nr_seq_criterio = nr_seq_criterio_p;
	end if;

else
	if (coalesce(ie_grau_satisfacao_p,'0') <> '0') then

		insert into man_os_crit_avaliacao(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_ordem_servico,
			nr_seq_criterio,
			ie_situacao,
			ie_grau_satisfacao)
		values (	nextval('man_os_crit_avaliacao_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_sequencia_p,
			nr_seq_criterio_p,
			'A',
			ie_grau_satisfacao_p);
	end if;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
