CREATE OR REPLACE PROCEDURE gerar_criterio_indicador ( nr_seq_criterio_p bigint, nr_seq_indicador_p bigint, ie_tipo_centro_p text, ie_considera_cc_base_p text, cd_tipo_rotina_p text, nm_usuario_p text) AS $body$
DECLARE

 
cd_centro_controle_dest_w		bigint;
cd_centro_controle_w		bigint;
cd_centro_base_w			bigint	:= null;
dt_referencia_w				timestamp;
qt_indicador_w				double precision;
cd_estabelecimento_w		integer;
cd_natureza_gasto_w			numeric(20);
nr_seq_ng_dest_w			bigint;
qt_total_indicador_w		double precision;
qt_distribuicao_w			double precision;
pr_distribuicao_w			double precision;
pr_total_distrib_w			double precision;
pr_dif_distrib_w			double precision;
qt_peso_w				double precision;
ds_erro_w				varchar(500);
ie_forma_obtencao_w			varchar(15);
nr_seq_tabela_w				tabela_custo.nr_sequencia%type;

c01 CURSOR FOR 
SELECT	cd_centro_controle 
from	centro_controle 
where	ie_tipo_centro_p	= 0 

union
 
SELECT	cd_centro_controle 
from 	centro_controle 
where	ie_tipo_centro_p	= 1 
and	ie_tipo_centro_controle	= '2' 

union
 
select	cd_centro_controle 
from	centro_controle 
where	ie_tipo_centro_p	= 2 
and	ie_centro_resultado	= 'S';

c02 CURSOR FOR 
SELECT	cd_centro_controle_dest, 
	qt_distribuicao, 
	qt_peso 
from 	criterio_distr_orc_dest 
where	cd_sequencia_criterio	= nr_seq_criterio_p;


BEGIN 
 
select	max(ie_forma_obtencao) 
into STRICT	ie_forma_obtencao_w 
from	resultado_indicador 
where	nr_sequencia	= nr_seq_indicador_p;
 
select	a.cd_centro_controle, 
	trunc(b.dt_mes_referencia,'month'), 
	a.cd_estabelecimento, 
	a.cd_natureza_gasto_dest, 
	a.nr_seq_ng_dest, 
	b.nr_sequencia 
into STRICT	cd_centro_base_w, 
	dt_referencia_w, 
	cd_estabelecimento_w, 
	cd_natureza_gasto_w, 
	nr_seq_ng_dest_w, 
	nr_seq_tabela_w 
from 	tabela_custo b, 
	criterio_distr_orc a 
where	a.cd_sequencia_criterio	= nr_seq_criterio_p 
and	a.cd_estabelecimento	= coalesce(b.cd_estabelecimento,a.cd_estabelecimento) 
and	a.cd_tabela_custo		= b.cd_tabela_custo;
 
 
if (ie_considera_cc_base_p = 'N') then 
	cd_centro_base_w	:= null;
end if;
 
delete 	from criterio_distr_orc_dest 
where 	cd_sequencia_criterio	= nr_seq_criterio_p;
commit;
 
open c01;
loop 
fetch	c01 into 
	cd_centro_controle_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	qt_indicador_w	:= 0;
	if (ie_forma_obtencao_w = 'S') then 
		begin 
		select coalesce(obter_indicador_cc( 
			cd_estabelecimento_w, 
			dt_referencia_w, 
			cd_centro_controle_w, 
			cd_centro_base_w, 
			cd_tipo_rotina_p, 
			nr_seq_tabela_w),0) 
		into STRICT	qt_indicador_w 
		;
		end;
	elsif (ie_forma_obtencao_w = 'B') then 
		begin 
		qt_indicador_w := cus_calcular_result_ind_sql(nr_seq_indicador_p, null, cd_estabelecimento_w, cd_centro_controle_w, dt_referencia_w, qt_indicador_w, nr_seq_tabela_w);
		end;
	end if;
	if (qt_indicador_w <> 0) then 
		begin 
		insert into criterio_distr_orc_dest( 
			cd_sequencia_criterio, 
			cd_estabelecimento, 
			cd_centro_controle_dest, 
			qt_distribuicao, 
			pr_distribuicao, 
			cd_natureza_gasto_dest, 
			dt_atualizacao, 
			nm_usuario, 
			qt_peso, 
			nr_seq_ng_dest, 
			dt_atualizacao_nrec, 
			nm_usuario_nrec) 
		values (	nr_seq_criterio_p, 
			cd_estabelecimento_w, 
			cd_centro_controle_w, 
			qt_indicador_w, 
			0, 
			cd_natureza_gasto_w, 
			clock_timestamp(), 
			nm_usuario_p, 
			1, 
			nr_seq_ng_dest_w, 
			clock_timestamp(), 
			nm_usuario_p);
		exception when others then 
			ds_erro_w	:= sqlerrm(SQLSTATE);
			CALL wheb_mensagem_pck.exibir_mensagem_abort(225508);
 
 
		end;
	end if;
	end;
end loop;
close c01;
 
select	coalesce(sum(qt_distribuicao),0) 
into STRICT	qt_total_indicador_w 
from 	criterio_distr_orc_dest 
where 	cd_sequencia_criterio	= nr_seq_criterio_p;
 
update	criterio_distr_orc 
set 	qt_base_distribuicao	= qt_total_indicador_w 
where	cd_sequencia_criterio	= nr_seq_criterio_p;
 
pr_total_distrib_w	:= 0;
 
open c02;
loop 
fetch	c02 into 
	cd_centro_controle_dest_w, 
	qt_distribuicao_w, 
	qt_peso_w;
EXIT WHEN NOT FOUND; /* apply on c02 */
	begin 
	pr_distribuicao_w		:= dividir((qt_distribuicao_w * 100),qt_total_indicador_w);
	pr_total_distrib_w		:= pr_total_distrib_w + pr_distribuicao_w;
 
	update	criterio_distr_orc_dest 
	set	pr_distribuicao		= pr_distribuicao_w, 
		qt_peso			= qt_peso_w 
	where	cd_sequencia_criterio	= nr_seq_criterio_p 
	and	cd_centro_controle_dest	= cd_centro_controle_dest_w;
 
	end;
end loop;
close c02;
 
pr_dif_distrib_w	:= 100 - pr_total_distrib_w;
 
if (pr_dif_distrib_w <> 0) then 
	update	criterio_distr_orc_dest 
	set	pr_distribuicao		= pr_distribuicao + pr_dif_distrib_w 
	where	cd_sequencia_criterio	= nr_seq_criterio_p 
	and	cd_centro_controle_dest	= cd_centro_controle_dest_w;
end if;
commit;
 
end;
$body$
