CREATE OR REPLACE PROCEDURE gerar_criterio_tre_phi (nr_seq_classif_p bigint, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w   bigint;

	C01 CURSOR FOR
	SELECT 	a.nr_sequencia
	from 	qms_classif_criterio a
	where 	a.nr_seq_classificacao = nr_seq_classif_p
	and 	not exists ( 	SELECT 1
				from 	qms_trein_classif_crit x
				where 	x.nr_seq_criterio = a.nr_sequencia
				and 	x.nr_seq_trein_classif = nr_sequencia_p);


BEGIN

if (nr_seq_classif_p IS NOT NULL AND nr_seq_classif_p::text <> '') then

	open C01;
	loop
	fetch C01 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		insert into 	qms_trein_classif_crit(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				nr_seq_trein_classif,
				nr_seq_criterio,
				dt_atualizacao_nrec,
				nm_usuario_nrec)
		values (
				nextval('qms_trein_classif_crit_seq'),
				clock_timestamp(),
				nm_usuario_p,
				nr_sequencia_p,
				nr_sequencia_w,
				clock_timestamp(),
				nm_usuario_p);
				commit;

		end;
	end loop;
	close C01;

end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
