SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_crm_atendimentos ( nr_atendimento_p bigint, cd_setor_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
cd_pessoa_fisica_w	varchar(10);
nm_pessoa_fisica_w	varchar(60);
nr_prontuario_w		varchar(10);
ds_setor_atendimento_w	varchar(100);
ie_tipo_atendimento_w	varchar(3);
ds_tipo_atendimento_w	varchar(255);
dt_entrada_w		varchar(100);
dt_alta_w		varchar(100);
cd_medico_resp_w	varchar(10);
nm_medico_resp_w	varchar(60);
cd_motivo_alta_w	varchar(3);
ds_motivo_alta_w	varchar(80);
ds_plano_convenio_w	varchar(80);
ds_convenio_w		varchar(255);
ds_categoria_w		varchar(80);
qt_crm_atendimentos_w	bigint;
					

BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (cd_setor_atendimento_p IS NOT NULL AND cd_setor_atendimento_p::text <> '') then 
 
	ds_setor_atendimento_w := obter_desc_setor_atend(cd_setor_atendimento_p);
 
	select	count(*) 
	into STRICT	qt_crm_atendimentos_w 
	from	crm_atendimentos 
	where	nr_atendimento = nr_atendimento_p;
 
	if (qt_crm_atendimentos_w > 0) then 
		update	crm_atendimentos 
		set	cd_setor_atendimento	= cd_setor_atendimento_p, 
			ds_setor_atendimento	= ds_setor_atendimento_w, 
			ie_status		= 2, 
			dt_atualizacao		= clock_timestamp(), 
			nm_usuario		= nm_usuario_p 
		where	nr_atendimento 		= nr_atendimento_p;
	else 
		select	cd_pessoa_fisica, 
			substr(obter_nome_pf(cd_pessoa_fisica),1,60), 
			substr(obter_prontuario_pf(cd_estabelecimento,cd_pessoa_fisica),1,10), 
			ie_tipo_atendimento, 
			substr(obter_nome_tipo_atend(ie_tipo_atendimento),1,255), 
			to_char(dt_entrada,'yyyy-mm-dd hh24:mi:ss'), 
			to_char(dt_alta,'yyyy-mm-dd hh24:mi:ss'), 
			cd_medico_resp, 
			substr(obter_nome_medico(cd_medico_resp,'N'),1,60), 
			cd_motivo_alta, 
			substr(obter_desc_motivo_alta(cd_motivo_alta),1,60) 
		into STRICT	cd_pessoa_fisica_w, 
			nm_pessoa_fisica_w, 
			nr_prontuario_w, 
			ie_tipo_atendimento_w, 
			ds_tipo_atendimento_w, 
			dt_entrada_w, 
			dt_alta_w, 
			cd_medico_resp_w, 
			nm_medico_resp_w, 
			cd_motivo_alta_w, 
			ds_motivo_alta_w		 
		from	atendimento_paciente 
		where	nr_atendimento = nr_atendimento_p;
		 
		select	max(substr(Obter_Desc_Plano_Conv(cd_convenio,cd_plano_convenio),1,80)), 
			max(substr(obter_nome_convenio(cd_convenio),1,255)), 
			max(substr(obter_categoria_convenio(cd_convenio,cd_categoria),1,80)) 
		into STRICT	ds_plano_convenio_w, 
			ds_convenio_w, 
			ds_categoria_w 
		from	atend_categoria_convenio 
		where	nr_seq_interno = Obter_Atecaco_atendimento(nr_atendimento_p);
		 
		insert into crm_atendimentos(	 
			nr_sequencia, 
			cd_pessoa_fisica, 
			nm_pessoa_fisica, 
			nr_prontuario, 
			nr_atendimento, 
			cd_setor_entrada, 
			ds_setor_entrada, 
			cd_setor_atendimento, 
			ds_setor_atendimento, 
			ie_tipo_atendimento, 
			ds_tipo_atendimento, 
			dt_entrada, 
			dt_alta, 
			cd_medico_resp, 
			nm_medico_resp, 
			ds_plano_convenio, 
			ds_convenio, 
			ds_categoria, 
			ds_motivo_alta, 
			cd_motivo_alta, 
			dt_atualizacao, 
			nm_usuario, 
			dt_atualizacao_nrec, 
			nm_usuario_nrec, 
			ie_status, 
			ie_exportado) 
		values (	nextval('crm_atendimentos_seq'), 
			cd_pessoa_fisica_w, 
			nm_pessoa_fisica_w, 
			nr_prontuario_w, 
			nr_atendimento_p, 
			cd_setor_atendimento_p, 
			ds_setor_atendimento_w, 
			cd_setor_atendimento_p, 
			ds_setor_atendimento_w, 
			ie_tipo_atendimento_w, 
			ds_tipo_atendimento_w, 
			dt_entrada_w, 
			dt_alta_w, 
			cd_medico_resp_w, 
			nm_medico_resp_w, 
			ds_plano_convenio_w, 
			ds_convenio_w, 
			ds_categoria_w, 
			ds_motivo_alta_w, 
			cd_motivo_alta_w, 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			'1', 
			'0');
	end if;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
