CREATE OR REPLACE PROCEDURE gerar_cta_pendencia_audit ( nr_seq_pendencia_p bigint, nr_seq_auditoria_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_audit_pend_w	bigint;


BEGIN

if (coalesce(nr_seq_pendencia_p,0) <> 0) and (coalesce(nr_seq_auditoria_p,0) <> 0) then

	select	count(*)
	into STRICT	qt_audit_pend_w
	from	cta_pendencia_audit
	where	nr_seq_pendencia = nr_seq_pendencia_p
	and	nr_seq_auditoria = nr_seq_auditoria_p;

	if (qt_audit_pend_w = 0) then

		insert into cta_pendencia_audit(
			nr_sequencia,
			nr_seq_pendencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_auditoria
		) values (
			nextval('cta_pendencia_audit_seq'),
			nr_seq_pendencia_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_auditoria_p
		);

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
