SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_d301_segmento_dpv ( nr_seq_dataset_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_seq_segmento_dpv	D301_SEGMENTO_DPV.nr_sequencia%type;
cd_versao_ops_w		D301_SEGMENTO_DPV.cd_versao_ops%type;
nr_atendimento_w	atendimento_paciente.nr_atendimento%type;


BEGIN

select	nr_atendimento
into STRICT	nr_atendimento_w
from	d301_dataset_envio
where	nr_sequencia = nr_seq_dataset_p;

select	max(to_char(b.dt_entrada,'yyyy'))
into STRICT	cd_versao_ops_w
from	atend_categoria_convenio a,
	atendimento_paciente b
where	a.nr_atendimento 	= b.nr_atendimento
and	a.nr_seq_interno 	= obter_atecaco_atendimento(nr_atendimento_w);

select 	nextval('d301_segmento_dpv_seq')
into STRICT	nr_seq_segmento_dpv
;

insert into D301_SEGMENTO_DPV(	cd_versao_cid,
				cd_versao_ops,
				dt_atualizacao,
				nm_usuario,
				nr_seq_dataset,
				nr_sequencia)
			values (	cd_versao_ops_w,
				cd_versao_ops_w,
				clock_timestamp(),
                                                                nm_usuario_p,
				nr_seq_dataset_p,
				nr_seq_segmento_dpv);

commit;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
