CREATE OR REPLACE PROCEDURE gerar_d301_segmento_ebg ( nr_seq_dataset_p bigint, nm_usuario_p text) AS $body$
DECLARE


  c01 CURSOR FOR
SELECT 	na.DT_NASCIMENTO as DS_DT_PARTO
from 	d301_dataset_envio   d3,
	atendimento_alta     aa,
	atendimento_paciente ap,
	nascimento           na
where 	d3.nr_sequencia   = nr_seq_dataset_p
and 	d3.nr_atendimento = aa.nr_atendimento
and 	aa.nr_atendimento = ap.nr_atendimento
and 	ap.nr_atendimento = na.nr_atendimento;

c01_w c01%rowtype;


BEGIN

open C01;
loop
fetch C01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	insert into D301_SEGMENTO_EBG(NR_SEQUENCIA,
		DT_ATUALIZACAO,
		NM_USUARIO,
		DT_ATUALIZACAO_NREC,
		NM_USUARIO_NREC,
		NR_SEQ_DATASET,
		DS_DT_PARTO)
	values (nextval('d301_segmento_ebg_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_dataset_p,
		c01_w.DS_DT_PARTO);
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
