CREATE OR REPLACE PROCEDURE gerar_d301_segmento_ezv (nr_seq_dataset_p bigint, nm_usuario_p text) AS $body$
DECLARE


  c01 CURSOR FOR
      SELECT ap.nr_atendimento,
             map.vl_material,
             null                                   nr_seq_301_remuneracao,
             substr(map.ds_observacao,1,70)         ds_justificativa,
             map.qt_material                        qt_pagamento,
             to_char(map.dt_atendimento,'yyyymmdd') dt_tratamento,
             null                                   vl_taxa
      from d301_dataset_envio        d3,
           atendimento_paciente      ap,
           material_atend_paciente   map
      where d3.nr_atendimento    = ap.nr_atendimento
      and   d3.nr_sequencia      = nr_seq_dataset_p
      and   map.nr_atendimento   = ap.nr_atendimento;

  c01_w c01%rowtype;


BEGIN
  open c01;
  fetch c01 into c01_w;
  loop

          insert into d301_segmento_ezv(nr_sequencia,
                                        dt_atualizacao,
                                        nm_usuario,
                                        dt_atualizacao_nrec,
                                        nm_usuario_nrec,
                                        nr_seq_dataset,
                                        nr_seq_301_remuneracao,
                                        ds_justificativa,
                                        qt_pagamento,
                                        dt_tratamento,
                                        vl_taxa,
                                        vl_material,
                                        nr_seq_dataset_ret) values (nextval('d301_segmento_ezv_seq'),
                                                                    clock_timestamp(),
                                                                    nm_usuario_p,
                                                                    clock_timestamp(),
                                                                    nm_usuario_p,
                                                                    nr_seq_dataset_p,
                                                                    c01_w.nr_seq_301_remuneracao,
                                                                    c01_w.ds_justificativa,
                                                                    c01_w.qt_pagamento,
                                                                    c01_w.dt_tratamento,
                                                                    c01_w.vl_taxa,
                                                                    c01_w.vl_material,
                                                                    null);

  fetch c01 into c01_w;
  EXIT WHEN NOT FOUND; /* apply on c01 */
  end loop;
  close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
