CREATE OR REPLACE PROCEDURE gerar_d301_segmento_fab ( nr_seq_dataset_p bigint, nm_usuario_p text) AS $body$
DECLARE



  ie_dataset_w       d301_dataset_envio.ie_dataset%type;
  nr_atendimento_w   d301_dataset_envio.nr_atendimento%type;


BEGIN

select	d3.ie_dataset,
	d3.nr_atendimento
into STRICT 	ie_dataset_w,
	nr_atendimento_w
from 	d301_dataset_envio d3
where 	d3.nr_sequencia = nr_seq_dataset_p;

if (ie_dataset_w = 'ENTL') then
	CALL gerar_d301_seg_fab_entl(nr_atendimento_w,nr_seq_dataset_p,nm_usuario_p);
elsif (ie_dataset_w = 'RECH') then
	CALL gerar_d301_seg_fab_rech(nr_atendimento_w,nr_seq_dataset_p,nm_usuario_p);
elsif (ie_dataset_w = 'VERL') then
	CALL gerar_d301_seg_fab_verl(nr_atendimento_w,nr_seq_dataset_p,nm_usuario_p);
end if;


end;
$body$
