SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_d301_segmento_fkt ( nr_seq_dataset_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_segmento_fkt_w		D301_SEGMENTO_FKT.nr_sequencia%type;
nr_seq_301_ident_processo_w	D301_SEGMENTO_FKT.nr_seq_301_ident_processo%type;
nr_seq_301_indic_cobr_ped_w	D301_SEGMENTO_FKT.nr_seq_301_indic_cobr_ped%type;
nr_transacao_w			D301_SEGMENTO_FKT.nr_transacao%type;

nr_atendimento_w		atendimento_paciente.nr_atendimento%type;
cd_cnes_estab_w			pessoa_juridica.cd_cnes%type;
cd_cnes_conv_w			pessoa_juridica.cd_cnes%type;


BEGIN

nr_seq_301_ident_processo_w 	:= 2; --Precisa definir
nr_seq_301_indic_cobr_ped_w 	:= 1; --Precisa definir
nr_transacao_w			:= '01';

select	nextval('d301_segmento_fkt_seq')
into STRICT	nr_seq_segmento_fkt_w
;

select	max(b.nr_atendimento)
into STRICT	nr_atendimento_w
from	d301_dataset_envio b
where	b.nr_sequencia = nr_seq_dataset_p;

if (coalesce(nr_atendimento_w,0) > 0) then
	select	max(c.cd_cnes)
	into STRICT	cd_cnes_estab_w
	from	atendimento_paciente a,
		estabelecimento b,
		pessoa_juridica c
	where	a.cd_estabelecimento 	= b.cd_estabelecimento
	and	b.cd_cgc		= c.cd_cgc
	and	a.nr_atendimento 	= nr_atendimento_w;

	select	substr(max(c.cd_cnes),1,9) cd_cnes
	into STRICT	cd_cnes_conv_w
	from	atend_categoria_convenio a,
		convenio b,
		pessoa_juridica c
	where	a.cd_convenio	= b.cd_convenio
	and	b.cd_cgc	= c.cd_cgc
	and	obter_atecaco_atendimento(nr_atendimento_w) = a.nr_seq_interno
	and	a.nr_atendimento	= nr_atendimento_w;

end if;

insert into D301_SEGMENTO_FKT(	dt_atualizacao,
				nm_usuario,
				nr_sequencia,
				nr_seq_301_ident_processo,
				nr_seq_301_indic_cobr_ped,
				nr_transacao,
				nr_ik_origem,
				nr_ik_destino,
				nr_seq_dataset)
			values (	clock_timestamp(),
				nm_usuario_p,
				nr_seq_segmento_fkt_w,
				nr_seq_301_ident_processo_w,
				nr_seq_301_indic_cobr_ped_w,
				nr_transacao_w,
				cd_cnes_estab_w,
				cd_cnes_conv_w,
				nr_seq_dataset_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
