SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_d301_segmento_unb ( nr_seq_arquivo_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		d301_segmento_unb.nr_sequencia%type;
d301_segmento_unb_w	d301_segmento_unb%rowtype;
cd_estabelecimento_w	d301_lote_envio.cd_estabelecimento%type;
cd_convenio_w		d301_lote_envio.cd_convenio%type;
ds_nome_arquivo_w	d301_arquivo_envio.ds_nome_arquivo%type;
nr_ik_hospital_w	varchar(9);
nr_ik_convenio_w	varchar(9);
dt_arquivo_w		timestamp;


BEGIN

select	max(a.cd_estabelecimento),
	max(coalesce(b.cd_convenio,a.cd_convenio)),
	max(b.ds_nome_arquivo)
into STRICT	cd_estabelecimento_w,
	cd_convenio_w,
	ds_nome_arquivo_w
from	d301_lote_envio a,
	d301_arquivo_envio b
where	a.nr_sequencia	= b.nr_seq_lote_envio
and	b.nr_sequencia	= nr_seq_arquivo_p;

select	substr(max(a.cd_cnes),1,9)
into STRICT	nr_ik_convenio_w
from	pessoa_juridica a,
	convenio b
where	a.cd_cgc	= b.cd_cgc
and	b.cd_convenio	= cd_convenio_w;

select	a.cd_cnes
into STRICT	nr_ik_hospital_w
from	pessoa_juridica a,
	estabelecimento b
where	a.cd_cgc		= b.cd_cgc
and	b.cd_estabelecimento	= cd_estabelecimento_w;

select	nextval('d301_segmento_unb_seq')
into STRICT	nr_sequencia_w
;

d301_segmento_unb_w.nr_sequencia	:= nr_sequencia_w;
d301_segmento_unb_w.dt_atualizacao	:= clock_timestamp();
d301_segmento_unb_w.nm_usuario		:= nm_usuario_p;
d301_segmento_unb_w.dt_atualizacao_nrec	:= clock_timestamp();
d301_segmento_unb_w.nm_usuario_nrec	:= nm_usuario_p;
d301_segmento_unb_w.nr_seq_arquivo	:= nr_seq_arquivo_p;

dt_arquivo_w := clock_timestamp();

d301_segmento_unb_w.ie_sintaxe		:= 'UNOC';
d301_segmento_unb_w.nr_versao_sintaxe	:= '3';
d301_segmento_unb_w.ds_remetente	:= nr_ik_hospital_w;
d301_segmento_unb_w.ds_destinatario	:= nr_ik_convenio_w;
d301_segmento_unb_w.ds_dt_criacao	:= to_char(dt_arquivo_w,'YYMMDD');
d301_segmento_unb_w.ds_hr_criacao	:= to_char(dt_arquivo_w,'HHMI');
d301_segmento_unb_w.nr_ref_arquivo_env	:= lpad(nr_seq_arquivo_p,5,'0');
d301_segmento_unb_w.nr_ref_arquivo_ret	:= null;
d301_segmento_unb_w.nr_referencia_aplic	:= ds_nome_arquivo_w;

insert into d301_segmento_unb values (d301_segmento_unb_w.*);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
