SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_dados_equipes (nr_cirurgia_p bigint, nr_seq_equipe_p bigint, nm_usuario_p text, nr_seq_procedimento_p bigint) AS $body$
DECLARE



c01 CURSOR FOR
	SELECT 	cd_pessoa_fisica,
		ie_funcao
	from   	pf_equipe_partic
	where  	nr_seq_equipe = nr_seq_equipe_p
	and	(ie_funcao IS NOT NULL AND ie_funcao::text <> '');

ie_funcao_w		varchar(10);
cd_pessoa_fisica_w	varchar(10);
nr_sequencia_w		bigint;


BEGIN




if (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') then
	open c01;
	loop
	fetch c01 into
		cd_pessoa_fisica_w,
		ie_funcao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		select	coalesce(max(nr_sequencia),0)+1
		into STRICT	nr_sequencia_w
		from	cirurgia_participante
		where	nr_cirurgia = nr_cirurgia_p;

		insert into cirurgia_participante(nr_sequencia,
				ie_funcao,
				nm_usuario,
				dt_atualizacao,
				nr_seq_interno,
				cd_pessoa_fisica,
				nr_cirurgia,
				nr_seq_procedimento)
		values (nr_sequencia_w,
				 ie_funcao_w,
				 nm_usuario_p,
				 clock_timestamp(),
				 nextval('cirurgia_participante_seq'),
				 cd_pessoa_fisica_w,
				 nr_cirurgia_p,
				 CASE WHEN nr_seq_procedimento_p=0 THEN null  ELSE nr_seq_procedimento_p END );
		end;
	end loop;
	close c01;
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
