SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_dados_trib_titulo (nr_titulo_p bigint, cd_tributo_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_periodicidade_w	varchar(1);
cd_variacao_w		varchar(2);


BEGIN

select	max(a.ie_periodicidade),
	max(a.cd_variacao)
into STRICT	ie_periodicidade_w,
	cd_variacao_w
from	tributo_dctf a
where	a.cd_tributo	= cd_tributo_p
and	a.dt_inicio_vigencia	= (SELECT	max(x.dt_inicio_vigencia)
				   from		tributo_dctf x
				   where	x.cd_tributo = a.cd_tributo
				   and		x.dt_inicio_vigencia <= clock_timestamp());
update	titulo_pagar
set	ie_periodicidade	= ie_periodicidade_w,
	cd_variacao		= cd_variacao_w
where	nr_titulo		= nr_titulo_p;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
