SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_data_fim_alerta (nr_sequencia_p bigint, dt_fim_p text, dt_fim_date_p timestamp DEFAULT NULL, ie_opcao_p text DEFAULT 'P') AS $body$
BEGIN
IF (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') AND (coalesce(ie_opcao_p,'P') = 'P') THEN

	IF (dt_fim_date_p IS NOT NULL AND dt_fim_date_p::text <> '') THEN
		UPDATE 	alerta_paciente
		SET 	dt_fim_alerta = dt_fim_date_p,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = wheb_usuario_pck.get_nm_usuario
		WHERE 	nr_sequencia = nr_sequencia_p;

	ELSE

		UPDATE 	alerta_paciente
		SET 	dt_fim_alerta = TO_DATE(dt_fim_p,'dd/mm/yyyy hh24:mi:ss'),
			dt_atualizacao = clock_timestamp(),
			nm_usuario = wheb_usuario_pck.get_nm_usuario
		WHERE 	nr_sequencia = nr_sequencia_p;

	END IF;
ELSE
	IF (dt_fim_date_p IS NOT NULL AND dt_fim_date_p::text <> '') THEN
		UPDATE 	atendimento_alerta
		SET 	dt_fim_alerta = dt_fim_date_p,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = wheb_usuario_pck.get_nm_usuario
		WHERE 	nr_sequencia = nr_sequencia_p;

	ELSE

		UPDATE 	atendimento_alerta
		SET 	dt_fim_alerta = TO_DATE(dt_fim_p,'dd/mm/yyyy hh24:mi:ss'),
			dt_atualizacao = clock_timestamp(),
			nm_usuario = wheb_usuario_pck.get_nm_usuario
		WHERE 	nr_sequencia = nr_sequencia_p;

	END IF;
END IF;

COMMIT;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
