CREATE OR REPLACE PROCEDURE gerar_dep_nec_vinc_proj_migr ( nr_seq_os_nec_vinc_p bigint, nr_seq_ativ_proj_p bigint, ie_nivel_dependencia_p text, ds_observacao_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_projeto_w	bigint;
ie_dep_reg_w		varchar(1);


BEGIN
if (nr_seq_os_nec_vinc_p IS NOT NULL AND nr_seq_os_nec_vinc_p::text <> '') and (ie_nivel_dependencia_p IS NOT NULL AND ie_nivel_dependencia_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	select	coalesce(max(nr_seq_proj),0)
	into STRICT	nr_seq_projeto_w
	from	proj_ordem_servico
	where	nr_seq_ordem = nr_seq_os_nec_vinc_p;

	if (nr_seq_projeto_w > 0) then
		begin
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_dep_reg_w
		from	proj_dependencia
		where	nr_seq_projeto = nr_seq_projeto_w
		and	nr_seq_ordem_serv = nr_seq_os_nec_vinc_p
		and	coalesce(nr_seq_ativ_cron,coalesce(nr_seq_ativ_proj_p,0)) = coalesce(nr_seq_ativ_proj_p,0);

		if (ie_dep_reg_w = 'N') then
			begin
			insert into proj_dependencia(
				nr_sequencia,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				dt_atualizacao,
				nm_usuario,
				nr_seq_projeto,
				nr_seq_ativ_cron,
				nr_seq_ordem_serv,
				nr_seq_proj_dep,
				ie_nivel_dependencia,
				ie_status,
				cd_funcao,
				ds_observacao)
			values (
				nextval('proj_dependencia_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_projeto_w,
				nr_seq_ativ_proj_p,
				nr_seq_os_nec_vinc_p,
				null,
				ie_nivel_dependencia_p,
				'N',
				null,
				ds_observacao_p);
			end;
		else
			begin
			CALL wheb_mensagem_pck.exibir_mensagem_abort(281865);
			end;
		end if;
		end;
	end if;
	end;
end if;
commit;
end;
$body$
