CREATE OR REPLACE PROCEDURE gerar_der_objeto ( nr_seq_der_p bigint, nm_usuario_p text, qt_topo_p bigint, qt_esquerda_p bigint, nm_objeto_p text, ie_tipo_objeto_p text, nm_tabela_origem_p text, nm_tabela_destino_p text, nr_sequencia_p INOUT bigint) AS $body$
DECLARE

nr_seq_obj_orig_w	bigint;
nr_seq_obj_dest_w	bigint;
ds_cor_w		varchar(10);


BEGIN
if	(nm_tabela_origem_p IS NOT NULL AND nm_tabela_origem_p::text <> '' AND nm_tabela_destino_p IS NOT NULL AND nm_tabela_destino_p::text <> '') then
	Select	nr_sequencia
	into STRICT	nr_seq_obj_orig_w
	from	des_der_objeto
	where	nr_seq_der = nr_seq_der_p
	and	upper(nm_objeto) = upper(nm_tabela_origem_p);


	Select	max(nr_sequencia)
	into STRICT	nr_seq_obj_dest_w
	from	des_der_objeto
	where	nr_seq_der = nr_seq_der_p
	and	upper(nm_objeto) = upper(nm_tabela_destino_p);
end if;

Select	CASE WHEN count(*)=0 THEN 'clSkyBlue'  ELSE 'clRed' END
into STRICT	ds_cor_w
from	tabela_sistema
where	upper(nm_tabela) = upper(nm_objeto_p);

Select	nextval('des_der_objeto_seq')
into STRICT	nr_sequencia_p
;

insert into des_der_objeto(	nr_sequencia,
				nr_seq_der,
				qt_topo,
				dt_atualizacao,
				nm_usuario,
				qt_esquerda,
				qt_largura,
				qt_altura,
				ds_cor,
				nm_objeto,
				ie_tipo_objeto,
				nr_seq_obj_origem,
				nr_seq_obj_destino)
			values (	nr_sequencia_p,
				nr_seq_der_p,
				qt_topo_p,
				clock_timestamp(),
				nm_usuario_p,
				qt_esquerda_p,
				Round(Length(nm_objeto_p)*10),
				80,
				ds_cor_w,
				nm_objeto_p,
				ie_tipo_objeto_p,
				nr_seq_obj_orig_w,
				nr_seq_obj_dest_w);

commit;

end;
$body$
