SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_desdobramento_proc_conta ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

	 
nr_seq_gerada_w		bigint;
qt_original_w		double precision := 0;
cd_convenio_w		integer;
i			integer	:= 1;
dt_procedimento_w	procedimento_paciente.dt_procedimento%type := null;
ie_seta_segundos_w   varchar(1);


BEGIN 
ie_seta_segundos_w := coalesce(Obter_valor_param_usuario(67, 736, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento),'N');
 
select 	coalesce(max(qt_procedimento),0), 
	coalesce(max(cd_convenio),0) 
into STRICT	qt_original_w, 
	cd_convenio_w 
from 	procedimento_paciente 
where 	nr_sequencia = nr_sequencia_p;
 
update	procedimento_paciente 
set 	qt_procedimento 	= 1, 
	nm_usuario 		= nm_usuario_p, 
	dt_atualizacao 		= clock_timestamp(), 
	ds_observacao 		= Wheb_mensagem_pck.get_Texto(305931) /*'Desdobramento Item -> Conta Paciente'*/
 
where 	nr_sequencia 		= nr_sequencia_p;
 
CALL atualiza_preco_procedimento(nr_sequencia_p, cd_convenio_w, nm_usuario_p);
 
for i in 1..(qt_original_w -1) loop 
	begin 
	 
	nr_seq_gerada_w := duplicar_proc_paciente(nr_sequencia_p, nm_usuario_p, nr_seq_gerada_w);
	 
	update	procedimento_paciente 
	set 	qt_procedimento 	= 1, 
		nm_usuario 		= nm_usuario_p, 
		dt_atualizacao 		= clock_timestamp(), 
		ds_observacao 		= Wheb_mensagem_pck.get_Texto(305931), /*'Desdobramento Item -> Conta Paciente'*/
 
		nr_seq_proc_desdob 	= nr_sequencia_p, 
		dt_procedimento		= coalesce(dt_procedimento_w,dt_procedimento) 
	where	nr_sequencia 		= nr_seq_gerada_w;
	 
		 
	if (ie_seta_segundos_w = 'S') then 
		begin 
		/*aqui vai diferenciar as datas dos procedimentos duplicadas em 3 segundos de diferen√ßa*/
 
		select	dt_procedimento + 3/86400 
		into STRICT	dt_procedimento_w 
		from 	procedimento_paciente 
		where 	nr_sequencia = nr_seq_gerada_w;
		exception 
		when others then 
			dt_procedimento_w := null;
		end;
	end if;
	 
	CALL atualiza_preco_procedimento(nr_seq_gerada_w, cd_convenio_w, nm_usuario_p);
	 
	end;
end loop;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
