SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_desdobramento_titulo (cd_estabelecimento_p bigint, nr_titulo_p bigint, vl_saldo_titulo_p bigint, dt_base_p timestamp, cd_condicao_pagto_p bigint, nm_usuario_p text) AS $body$
DECLARE



ie_forma_pagamento_w		condicao_pagamento.ie_forma_pagamento%type		:= 0;
ie_acao_venc_nao_util_w		condicao_pagamento.ie_acao_nao_util%type		:= 'M';
cd_moeda_padrao_w			parametro_contas_receber.cd_moeda_padrao%type	:= 0;
vl_vencimento_w				double precision;
tx_fracao_parcela_w			double precision:= 0;
tx_acrescimo_w				double precision:= 0;
nr_sequencia_w				titulo_receber_desdob.nr_sequencia%type;
dt_base_w					timestamp;
dt_vencimento_w				timestamp;
ie_tipo_acrescimo_w			condicao_pagamento.ie_tipo_acrescimo%type		:= 'P';
vl_base_juros_w				double precision;

c01 CURSOR FOR
	SELECT	/* A Prazo   */
             	tx_fracao_parcela,
             	coalesce(tx_acrescimo,0),
		Obter_data_vencimento(
            			dt_base_p,
				qt_dias_parcela,
				cd_estabelecimento_p,
				ie_corrido_util,
				ie_acao_venc_nao_util_w),
		0
	from 	parcela
	where 	cd_condicao_pagamento 	= cd_condicao_pagto_p
	and 	ie_forma_pagamento_w	<> 1
	
union

	SELECT 	100, 0,	/* A Vista   */
		dt_base_p, 0
	
	where 	ie_forma_pagamento_w	= 1;



BEGIN

select 	coalesce(max(ie_forma_pagamento),3),
	coalesce(max(ie_acao_nao_util),'M'),
	coalesce(max(ie_tipo_acrescimo),'P')
into STRICT 	ie_forma_pagamento_w,
	ie_acao_venc_nao_util_w,
	ie_tipo_acrescimo_w
from 	condicao_pagamento
where 	cd_condicao_pagamento = cd_condicao_pagto_p;

select 	coalesce(max(cd_moeda_padrao),1)
into STRICT 	cd_moeda_padrao_w
from 	parametro_contas_receber
where	cd_estabelecimento  = cd_estabelecimento_p;

Select coalesce(Obter_Data_Base_Venc(dt_base_p, ie_forma_pagamento_w), dt_base_w)
into STRICT  dt_base_w
;

delete from titulo_receber_desdob
where	nr_titulo	= nr_titulo_p;

if (ie_tipo_acrescimo_w = 'B') then
	vl_base_juros_w	:= obter_base_acresc_cond_pagto(cd_condicao_pagto_p,vl_saldo_titulo_p);
end if;

OPEN	c01;
LOOP
FETCH	c01 into
	tx_fracao_parcela_w,
	tx_acrescimo_w,
	dt_vencimento_w,
	vl_vencimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin


	if (vl_vencimento_w = 0) then
		if (coalesce(tx_fracao_parcela_w,0) > 0) then
       			vl_vencimento_w 	:= ((vl_saldo_titulo_p * tx_fracao_parcela_w) / 100);
		end if;

	if (tx_acrescimo_w <> 0) then
		if (ie_tipo_acrescimo_w = 'P') then
       			vl_vencimento_w 	:= vl_vencimento_w + ((vl_vencimento_w * tx_acrescimo_w) / 100);
		elsif (ie_tipo_acrescimo_w = 'B') then
			vl_vencimento_w 	:= vl_vencimento_w + ((vl_base_juros_w * tx_acrescimo_w) / 100);
		end if;
	end if;

	select	nextval('titulo_receber_desdob_seq')
	into STRICT	nr_sequencia_w
	;

	insert	into titulo_receber_desdob(nr_sequencia,
		nr_titulo,
		dt_vencimento,
		vl_titulo,
		dt_atualizacao,
		nm_usuario,
		nr_titulo_dest,
		vl_saldo_juros,
		vl_saldo_multa)
	values (nr_sequencia_w,
		nr_titulo_p,
		dt_vencimento_w,
		vl_vencimento_w,
		clock_timestamp(),
		nm_usuario_p,
		null,
		0,
		0);

	end if;

	end;

end	loop;
close	c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_desdobramento_titulo (cd_estabelecimento_p bigint, nr_titulo_p bigint, vl_saldo_titulo_p bigint, dt_base_p timestamp, cd_condicao_pagto_p bigint, nm_usuario_p text) FROM PUBLIC;

