SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_devolucao_oper_terc (nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_material_w			bigint;
nr_sequencia_w			bigint;
dt_atualizacao_estoque_w	timestamp;


BEGIN

select	max(cd_material),
	max(dt_atualizacao_estoque)
into STRICT	cd_material_w,
	dt_atualizacao_estoque_w
from	terceiro_operacao
where	nr_sequencia	= nr_sequencia_p;

select	nextval('terceiro_operacao_seq')
into STRICT	nr_sequencia_w
;

insert	into terceiro_operacao(nr_sequencia,
	cd_estabelecimento,
	nr_seq_terceiro,
	nr_seq_operacao,
	tx_operacao,
	dt_atualizacao,
	nm_usuario,
	nr_doc,
	nr_seq_doc,
	dt_operacao,
	nr_seq_conta,
	cd_material,
	ie_origem_proced,
	cd_procedimento,
	qt_operacao,
	vl_operacao,
	nr_lote_contabil,
	cd_pessoa_fisica,
	dt_atualizacao_estoque,
	cd_local_estoque,
	nr_seq_trans_fin,
	nr_seq_ordem_servico,
	dt_inclusao,
	nm_usuario_inclusao,
	ie_biometria,
	ie_senha,
	qt_peso,
	cd_cgc_fornecedor,
	nr_seq_oper_origem,
	NR_SEQ_LOTE_FORNEC)
SELECT	nr_sequencia_w,
	cd_estabelecimento,
	nr_seq_terceiro,
	nr_seq_operacao,
	tx_operacao,
	clock_timestamp(),
	nm_usuario_p,
	nr_doc,
	nr_seq_doc,
	dt_operacao,
	nr_seq_conta,
	cd_material,
	ie_origem_proced,
	cd_procedimento,
	qt_operacao,
	vl_operacao * -1,
	nr_lote_contabil,
	cd_pessoa_fisica,
	null,
	cd_local_estoque,
	nr_seq_trans_fin,
	nr_seq_ordem_servico,
	dt_inclusao,
	nm_usuario_p,
	ie_biometria,
	ie_senha,
	qt_peso,
	cd_cgc_fornecedor,
	nr_sequencia_p,
	NR_SEQ_LOTE_FORNEC
from	terceiro_operacao
where	nr_sequencia	= nr_sequencia_p;

if (cd_material_w IS NOT NULL AND cd_material_w::text <> '') and (dt_atualizacao_estoque_w IS NOT NULL AND dt_atualizacao_estoque_w::text <> '') then
	CALL Gerar_movto_estoque_Terc(nr_sequencia_w, '2', nm_usuario_p); --Gerar devolução no estoque
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
