SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_diagnosticos_atend ( nr_atendimento_p bigint, ds_lista_doencas_p text, cd_medico_p text, nm_usuario_p diagnostico_doenca.nm_usuario%type, cd_departamento_med_p diagnostico_doenca.CD_DEPARTAMENTO_MED%type default null, nr_seq_interno_old_p diagnostico_doenca.nr_seq_interno%type default null) AS $body$
DECLARE


ds_lista_w		varchar(1000);
ie_pos_virgula_w	smallint;
cd_doenca_w		varchar(10) := '';
cd_medico_w		varchar(10)	:= cd_medico_p;
outcome_exists_w varchar(10);
ie_submit_w varchar(1) := 'N';
dt_liberacao_w timestamp := null;

BEGIN

ie_submit_w := Obter_param_Usuario(281, 1597, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_submit_w);

if (ie_submit_w = 'S') then
 dt_liberacao_w := clock_timestamp();
end if;
ds_lista_w	:= ds_lista_doencas_p;

if (coalesce(cd_medico_w::text, '') = '') then
	cd_medico_w	:= obter_pessoa_fisica_usuario(nm_usuario_p,'C');
end if;



if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (ds_lista_doencas_p IS NOT NULL AND ds_lista_doencas_p::text <> '') and (cd_medico_w IS NOT NULL AND cd_medico_w::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	
		
	
	while(ds_lista_w IS NOT NULL AND ds_lista_w::text <> '') loop
		begin
		
		ie_pos_virgula_w 	:= position(',' in ds_lista_w);
		
		if (ie_pos_virgula_w <> 0) then
			begin
			cd_doenca_w	:= substr(ds_lista_w,1,(ie_pos_virgula_w - 1));
			ds_lista_w	:= substr(ds_lista_w,(ie_pos_virgula_w + 1),length(ds_lista_w));
			end;
		else
			begin
			cd_doenca_w	:= ds_lista_w;
			ds_lista_w	:= null;
			end;
		end if;		
		select count(1)
		into STRICT outcome_exists_w
		from DIAGNOSTICO_DOENCA_HIST z
		where((z.nr_atendimento = nr_atendimento_p) or  z.cd_pessoa_fisica = obter_pessoa_atendimento(nr_atendimento_p,'C'))
		and z.cd_doenca = cd_doenca_w and (z.DT_DISEASE_END IS NOT NULL AND z.DT_DISEASE_END::text <> '' AND z.IE_STATUS IS NOT NULL AND z.IE_STATUS::text <> '');

		if (coalesce(pkg_i18n.get_user_locale, 'pt_BR') = 'ja_JP' and outcome_exists_w = 0 ) then
		  CALL wheb_mensagem_pck.exibir_mensagem_abort(1130932);
		else
		  CALL gerar_diagnostico_atend(
			 nr_atendimento_p,
			 cd_doenca_w,
			 cd_medico_w,
			 nm_usuario_p,
			 2,
			 'S',
             dt_liberacao_w,
			 null,
			 null,
			 null,
             cd_departamento_med_p,
             nr_seq_interno_old_p);
		end if;
		end;
	end loop;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
