CREATE OR REPLACE PROCEDURE gerar_diagnostico_apae ( nm_usuario_p text, nr_seq_aval_pre_p bigint, nr_seq_diagnostico_p bigint) AS $body$
DECLARE


cd_doenca_cid_w	varchar(10);


BEGIN

select	max(cd_doenca_cid)
into STRICT	cd_doenca_cid_w
from	apa_diagnostico
where	nr_sequencia = nr_seq_diagnostico_p;

insert into aval_pre_anest_diag(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_diag,
	nr_seq_aval_pre,
	ie_paciente,
	CD_DOENCA_CID)
SELECT	nextval('aval_pre_anest_diag_seq'),
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_diagnostico_p,
	nr_seq_aval_pre_p,
	'S',
	cd_doenca_cid_w

where	not exists (	select	1
			from	aval_pre_anest_diag
			where	nr_seq_diag	=	nr_seq_diagnostico_p
			and	nr_seq_aval_pre	=	nr_seq_aval_pre_p);

commit;

end;
