SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_diagnostico_checkup ( nr_atendimento_p bigint, nr_seq_diag_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text, nr_seq_checkup_etapa_p bigint default null, ie_liberar_p text default null) AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_seq_apres_w		integer;


BEGIN

if (nr_atendimento_p = 0) then
	-- Para gerar diagnosticos e necessario ter um atendimento selecionado.
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(264457);
end if;

select	nextval('checkup_diagnostico_seq')
into STRICT	nr_sequencia_w
;

select	nr_seq_apres
into STRICT	nr_seq_apres_w
from	checkup_tipo_diag
where	nr_sequencia = nr_seq_diag_p;

insert into checkup_diagnostico(
	nr_sequencia,
	nr_atendimento,
	dt_atualizacao,
	nm_usuario,
	nr_seq_diag,
	cd_pessoa_fisica,
	dt_registro,
	nr_seq_impressao,
	ie_situacao,
	nr_seq_checkup_etapa)
values (
	nr_sequencia_w,
	nr_atendimento_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_diag_p,
	cd_pessoa_fisica_p,
	clock_timestamp(),
	nr_seq_apres_w,
	'A',
	nr_seq_checkup_etapa_p);

insert into checkup_diag_pac_orient(
	nr_sequencia,
	nr_seq_diag,
	dt_atualizacao,
	nm_usuario,
	nr_seq_orientacao,
	ie_relatorio,
	ie_situacao)
SELECT	nextval('checkup_diag_pac_orient_seq'),
	nr_sequencia_w,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_orientacao,
	ie_relatorio,
	'A'
from	checkup_diag_orient
where	nr_seq_diag = nr_seq_diag_p;
if (ie_liberar_p = 'S')then
    CALL liberar_informacao('CHECKUP_DIAGNOSTICO', 'NR_SEQUENCIA', nr_sequencia_w, nm_usuario_p);
end if;

commit;

END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
