CREATE OR REPLACE PROCEDURE gerar_diagnostico_doenca_reg ( nr_atendimento_p bigint, cd_doenca_cid_p text, dt_diagnostico_p timestamp, cd_medico_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (coalesce(nr_atendimento_p,0) > 0) and (cd_doenca_cid_p IS NOT NULL AND cd_doenca_cid_p::text <> '')  then

	insert into  diagnostico_medico(
		nr_atendimento,
		dt_diagnostico,
		ie_tipo_diagnostico,
		cd_medico,
		dt_atualizacao,
		nm_usuario)
	values (
		nr_atendimento_p,
		dt_diagnostico_p,
		2,
		cd_medico_p,
		clock_timestamp(),
		nm_usuario_p);
	commit;


	insert into diagnostico_doenca(
		nr_atendimento,
		dt_diagnostico,
		cd_doenca,
		dt_atualizacao,
		nm_usuario,
		ie_tipo_diagnostico,
		ie_classificacao_doenca)
	values (
		nr_atendimento_p,
		dt_diagnostico_p,
		cd_doenca_cid_p,
		clock_timestamp(),
		nm_usuario_p,
		2,
		'P');
end if;

commit;

end;
$body$
