CREATE OR REPLACE PROCEDURE gerar_diagnostico_doenca (nr_atendimento_p bigint, cd_doenca_cid_p text, dt_diagnostico_p timestamp, cd_medico_p bigint, nm_usuario_p text, nr_seq_consulta_form_p bigint default null) AS $body$
BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (cd_doenca_cid_p IS NOT NULL AND cd_doenca_cid_p::text <> '')  then

	insert into  diagnostico_medico(
		nr_atendimento,                 
		dt_diagnostico,                 
		ie_tipo_diagnostico,
		cd_medico,                      
		dt_atualizacao,                 
		nm_usuario)
	values (
		nr_atendimento_p,
		dt_diagnostico_p,		
		1,
		cd_medico_p,
		clock_timestamp(),
		nm_usuario_p);
	commit;
		
	
	insert into diagnostico_doenca(
		nr_atendimento,
		dt_diagnostico,         
		cd_doenca,              
		dt_atualizacao,
		nm_usuario,
		ie_tipo_diagnostico,
		ie_classificacao_doenca,
		nr_seq_consulta_form,
		ie_situacao)        
	values (
		nr_atendimento_p,
		dt_diagnostico_p,
		cd_doenca_cid_p,
		clock_timestamp(),
		nm_usuario_p,
		1,
		'P',
		nr_seq_consulta_form_p,
		'A');
end if;

commit;

end;
$body$
